/**
 * Function node (not Function Item)
 * Processes all incoming items and returns one item per job.
 */
const inputs = $input.all();

const safeMultiDecode = (str, times = 3) => {
  let prev = str, cur = str;
  for (let i = 0; i < times; i++) {
    try { cur = decodeURIComponent(prev); } catch { break; }
    if (cur === prev) break;
    prev = cur;
  }
  return cur;
};

const tryExtractParam = (urlStr) => {
  if (typeof urlStr !== "string" || !urlStr) return "";
  try {
    const u = new URL(urlStr);
    const param = u.searchParams.get("url");
    if (param) return safeMultiDecode(param);
    if (u.hostname.includes("upwork.com")) return urlStr;
    return "";
  } catch {
    const part = urlStr.split("url=")[1];
    return part ? safeMultiDecode(part) : "";
  }
};

const formatPosted = (pubDateStr) => {
  if (typeof pubDateStr !== "string" || !pubDateStr.trim()) {
    return { posted: "", date: "" };
  }
  const pubDate = new Date(pubDateStr);
  if (isNaN(pubDate.getTime())) return { posted: "", date: "" };

  const now = new Date();
  const diffMs = now - pubDate;
  const diffMinutes = Math.floor(diffMs / 60000);
  const diffHours = Math.floor(diffMinutes / 60);
  const diffDays = Math.floor(diffHours / 24);

  let posted = "";
  if (diffMinutes < 1) posted = "Posted just now";
  else if (diffMinutes < 60) posted = `Posted ${diffMinutes} minute${diffMinutes !== 1 ? "s" : ""} ago`;
  else if (diffHours < 24) posted = `Posted ${diffHours} hour${diffHours !== 1 ? "s" : ""} ago`;
  else posted = `Posted ${diffDays} day${diffDays !== 1 ? "s" : ""} ago`;

  const formattedDate = pubDate.toLocaleDateString("en-GB", { day: "numeric", month: "long", year: "numeric" });
  return { posted, date: formattedDate };
};

const outputs = inputs.map(({ json }) => {
  const out = {};

  // 1) Title → { title, budget }
  (() => {
    const input = json?.title;
    if (typeof input !== "string") {
      out.title = "";
      out.budget = "";
      return;
    }
    const regex = /^(.*?)\s*\((.*?)\)$/;
    const match = input.match(regex);
    out.title = match ? match[1].trim() : input;
    out.budget = match ? match[2].trim() : "";
  })();

  // 2) Link → upwork_link
  out.upwork_link = tryExtractParam(json?.link) || "";

  // 3) pubDate → { posted, date }
  Object.assign(out, formatPosted(json?.pubDate));

  // 4) content → { job_description, skills, categories }
  (() => {
    const raw = json?.content;
    if (typeof raw !== "string" || !raw) {
      out.job_description = "";
      out.skills = "";
      out.categories = "";
      return;
    }
    const text = raw
      .replace(/&amp;/g, "&")
      .replace(/&nbsp;/g, " ")
      .replace(/\r\n/g, "\n")
      .replace(/\r/g, "\n");

    const skillsMatch = text.match(/^\s*Skills:\s*([^\n\r]+)/im);
    const categoriesMatch = text.match(/^\s*Categories:\s*([^\n\r]+)/im);

    out.skills = skillsMatch ? skillsMatch[1].trim() : "";
    out.categories = categoriesMatch ? categoriesMatch[1].trim() : "";

    out.job_description = text
      .replace(/^\s*Skills:.*$/gim, "")
      .replace(/^\s*Categories:.*$/gim, "");
  })();

  return { json: out };
});

return outputs;
