/**
 * n8n Code (Code v2)
 * Behavior:
 * - Looks for today's date (Asia/Kolkata). If any "Date N" already equals today, it updates that same N.
 * - Otherwise, it finds the first empty "Date N" and writes there.
 * - If domain not found in SERP → position = 100 and link = null.
 * - Prevents overwriting filled dates unless it's the same day (unless you enable OVERWRITE_IF_ALL_FILLED).
 *
 * Requires:
 * - A node named "Get Targeted Keywords" (or adapt the selector below) that returns the row with `row_number` (or `rowNumber`)
 * - Columns in the sheet:
 *   Ranking Date 1..12, Ranking URL Date 1..12, Date 1..12
 */

const MAX_SLOTS = 12;                // ✅ now 12
const OVERWRITE_IF_ALL_FILLED = false; // set true to overwrite the last slot if all are filled

// ---- Target domain / URL detection ----
// Option A: hardcode a domain (kept for compatibility)
const HARDCODED_DOMAIN = "Update Your Domain";

// --- helpers ---
function dateInIST(d = new Date()) {
  const istOffsetMin = 330; // +05:30
  return new Date(d.getTime() + (istOffsetMin - d.getTimezoneOffset()) * 60000);
}
function todayISTString() {
  return dateInIST().toISOString().split("T")[0]; // YYYY-MM-DD
}
function safeTrim(v) {
  return (v ?? "").toString().trim();
}
function getDomainFromUrl(u) {
  try {
    const url = new URL(u);
    // normalize to scheme + host (like https://example.com/)
    return `${url.protocol}//${url.host}/`;
  } catch {
    return "";
  }
}

// --- get the sheet row (rename the node name if needed) ---
const row = $('Get Targeted Keywords').first().json || {};
const rowNumber = row.rowNumber ?? row.row_number;
if (!rowNumber) {
  return [{ json: { error: "Missing rowNumber/row_number from sheet row.", debug: row } }];
}

// Try to use the exact page/target URL from sheet if present
const targetUrl = row["Target Page"] || row["Page"] || "";
const inferredDomain = getDomainFromUrl(targetUrl);
const targetDomain = inferredDomain || HARDCODED_DOMAIN;

// --- parse SERP (expects this node input to have an 'organic' array with {link}) ---
const organicResults = $json.organic || [];

// Defaults when NOT found
let foundDomain = false;
let foundExactUrl = false;
let position = 100;
let link = null;

for (let i = 0; i < organicResults.length; i++) {
  const currentLink = organicResults[i].link || "";
  if (!currentLink) continue;

  if (currentLink.includes(targetDomain)) {
    foundDomain = true;
    position = i + 1;      // actual rank
    link = currentLink;    // actual URL
    if (safeTrim(targetUrl) && currentLink === targetUrl) foundExactUrl = true;
    break;
  }
}

// --- choose the Date slot ---
const today = todayISTString();

// 1) If any Date N already equals today, update that same N
let slot = null;
for (let i = 1; i <= MAX_SLOTS; i++) {
  const dateKey = `Date ${i}`;
  const val = safeTrim(row[dateKey]);
  if (val === today) {
    slot = i;
    break;
  }
}

// 2) Else pick the first empty Date N
if (slot === null) {
  for (let i = 1; i <= MAX_SLOTS; i++) {
    const dateKey = `Date ${i}`;
    const val = safeTrim(row[dateKey]);
    if (val === "") {
      slot = i;
      break;
    }
  }
}

// 3) If all filled
if (slot === null) {
  if (!OVERWRITE_IF_ALL_FILLED) {
    return [{
      json: {
        error: `All ${MAX_SLOTS} date slots are filled for row ${rowNumber}. Add more columns or enable OVERWRITE_IF_ALL_FILLED.`,
        rowNumber
      }
    }];
  } else {
    slot = MAX_SLOTS; // overwrite the last slot
  }
}

// Build dynamic keys for the chosen slot
const rankCol = `Ranking Date ${slot}`;
const urlCol  = `Ranking URL Date ${slot}`;
const dateCol = `Date ${slot}`;

// Minimal payload to update ONLY these three cells
return [{
  json: {
    rowNumber,
    [rankCol]: position, // actual rank or 100 if not found
    [urlCol]: link,      // actual URL or null if not found
    [dateCol]: today,    // execution date (IST)
    _debug: { foundDomain, foundExactUrl, position, link, targetUrl, inferredDomain: targetDomain, slot, today }
  }
}];

/**
 * INPUTS (from a Merge node or two incoming connections):
 *   A) update payload item (e.g. { rowNumber, "Ranking Date 1": 1, "Ranking URL Date 1": "...", "Date 1": "YYYY-MM-DD" })
 *   B) sheet row item       (e.g. { row_number: 2, Page, Keyword, "Ranking Date 1", "Date 1", ... })
 *
 * OUTPUT:
 *   Single item with:
 *     - rowNumber
 *     - ALL existing columns from the sheet row
 *     - overlaid updated fields (only the changed date/week keys)
 *
 * Notes:
 *   - Works with either Week or Date column schemes.
 *   - Keeps nulls as null (no coercion), so link can stay null when not found.
 */

const items = $input.all().map(i => i.json);

// Detectors
function looksLikeRow(obj) {
  return (('row_number' in obj) || ('rowNumber' in obj)) && ('Keyword' in obj || 'Page' in obj);
}
function looksLikeUpdate(obj) {
  const keys = Object.keys(obj);
  // date scheme
  const hasRankDate = keys.some(k => /^Ranking Date \d+$/.test(k));
  const hasUrlDate  = keys.some(k => /^Ranking URL Date \d+$/.test(k));
  const hasDate     = keys.some(k => /^Date \d+$/.test(k));
  // week scheme (kept for backwards-compat)
  const hasRankWeek = keys.some(k => /^Ranking Week \d+$/.test(k));
  const hasUrlWeek  = keys.some(k => /^Ranking URL Week \d+$/.test(k));
  return (hasRankDate || hasUrlDate || hasDate || hasRankWeek || hasUrlWeek);
}

const row = items.find(looksLikeRow) || {};
const upd = items.find(looksLikeUpdate) || {};

const rowNumber = row.rowNumber ?? row.row_number ?? upd.rowNumber;
if (!rowNumber) {
  return [{ json: { error: "rowNumber missing. Ensure the sheet row and update payload reach this node.", debug: items } }];
}

// Start with rowNumber
const payload = { rowNumber };

// 1) copy ALL existing columns from the sheet row
for (const [k, v] of Object.entries(row)) {
  if (k === 'row_number' || k === 'rowIndex' || k === 'rowNumber') continue; // internal keys
  payload[k] = v;
}

// 2) overlay ONLY updated fields from the update item
for (const [k, v] of Object.entries(upd)) {
  if (k === 'row_number' || k === 'rowNumber' || k.startsWith('_')) continue; // skip internal/meta
  // leave nulls as null (so link can stay null when domain not found)
  payload[k] = v;
}

return [{ json: payload }];
