const item = items[0].json;

// Combine and stringify possible text-containing fields
let input = item.text || item.message || item.caption || item.input || item.user_input || "";

// Convert non-string inputs (like objects) to a string
if (typeof input !== "string") {
  try {
    input = JSON.stringify(input);
  } catch (err) {
    input = "";
  }
}

// Regex to extract Instagram Reel URL
const match = input.match(/(?:https?:\/\/)?(?:www\.)?instagram\.com\/reel\/[a-zA-Z0-9_-]+/i);

let cleanedUrl = null;

if (match && match[0]) {
  const baseUrl = match[0].startsWith("http") ? match[0] : `https://${match[0]}`;
  cleanedUrl = baseUrl.split("?")[0].replace(/\/$/, "");
}

return [
  {
    json: {
      original_input: input,
      reel_url: cleanedUrl
    }
  }
];
