const text = $input.first().json.message.text;

// Split text to notes and tasks
const [notesPart, tasksPart] = text.split(/Tasks:/i);

// Exctact notes
const notes = notesPart.trim();

// Exctact tasks
const tasks = tasksPart
  ? tasksPart
      .split('\n')
      .map(line => line.trim())
      .filter(line => line.startsWith('- '))
      .map(line => line.replace(/^- /, '').trim())
  : [];

return [{
  json: {
    notes: notes,
    tasks: tasks
  }
}];

return items.map(item => {
  const task = item.json.tasks;
  // Extract only task names
  const title = task.replace(/^-?\s*/, '').replace(/\s*\(Assignee:.*?\)/, '').trim();
  return {
    json: {
      title: title
    }
  };
});
