// Group photos by date
const photosByDate = {};

for (const item of $input.all()) {
  const dateFormatted = new Date (item.json.timestamp).toDateString();
  const date = dateFormatted;
  if (!photosByDate[date]) {
    photosByDate[date] = [];
  }
  photosByDate[date].push(item.json);
}

// Sort dates and create day entries
const sortedDates = Object.keys(photosByDate).sort();
const dayEntries = [];

for (const date of sortedDates) {
  const photos = photosByDate[date];

  // Sort photos by timestamp within each day
  photos.sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));

  // Extract themes for the day
  const dayThemes = [...new Set(photos.flatMap(p => p.themes))];

  dayEntries.push({
    date: date,
    dayNumber: sortedDates.indexOf(date) + 1,
    photos: photos,
    themes: dayThemes,
    photoCount: photos.length
  });
}

return [{
  json: {
    tripDays: dayEntries,
    totalDays: dayEntries.length,
    totalPhotos: $input.all().length,
    dateRange: {
      start: sortedDates[0],
      end: sortedDates[sortedDates.length - 1]
    }
  }
}];
