let input = items[0].json;
let output = {};

// Utility: Extract domain from email
function extractDomain(email) {
  return typeof email === 'string' && email.includes('@')
    ? email.split('@')[1].toLowerCase()
    : null;
}

// Case 1: Direct object (Typeform-style)
if (input?.Name && input?.Email) {
  output = {
    name: input["Name"] || null,
    email: input["Email"] || null,
    phone: input["Phone Number"] || null,
    message: input["Message"] || null,
    domain: extractDomain(input["Email"]),
    source: "Typeform"
  };
}

// Case 2: Array of one object (Typeform-style)
else if (Array.isArray(input) && input[0]?.Name && input[0]?.Email) {
  const data = input[0];
  output = {
    name: data["Name"] || null,
    email: data["Email"] || null,
    phone: data["Phone Number"] || null,
    message: data["Message"] || null,
    domain: extractDomain(data["Email"]),
    source: "Typeform"
  };
}

// Case 3: Calendly payload
else if (input?.event === "invitee.created" && input?.payload) {
  const payload = input.payload;
  const email = payload.email || null;
  output = {
    name: payload.name || `${payload.first_name || ""} ${payload.last_name || ""}`.trim(),
    email: email,
    phone: payload.text_reminder_number || null,
    message: payload.questions_and_answers?.[0]?.answer || null,
    domain: extractDomain(email),
    source: "Calendly"
  };
}

// Else: unsupported
else {
  output = {
    error: "Unsupported input format",
    inputType: typeof input,
    preview: JSON.stringify(input)
  };
}

return [{ json: output }];

