const input = $json;

const headers = input.headers || {};
const query = input.query || {};

if (!headers.authorization) {
  return [{ json: { success: false, reason: "Missing Authorization header" } }];
}

if (!headers.authorization.startsWith("Bearer ")) {
  return [{ json: { success: false, reason: "Authorization header must start with 'Bearer '" } }];
}

if (!query.hasOwnProperty("job_id")) {
  return [{ json: { success: false, reason: "Missing 'job_id' in query" } }];
}

if (Object.keys(query).length !== 1) {
  return [{ json: { success: false, reason: "Query must contain only 'job_id'" } }];
}

return [{ json: { success: true } }];

const record = $json;

const keyMapping = {
    "id": "id",
    "createdTime": "created_time",
    "Job Title": "job_title",
    "Location": "location",
    "Job Type": "job_type",
    "Company Name": "company_name",
    "Number of Applicants": "applicants",
    "Salary Midpoint": "salary"
};

let jobObject = { success: true };

for (let key in keyMapping) {
    if (record[key] !== undefined) {
        jobObject[keyMapping[key]] = record[key];
    }
}

return [{
    json: {
        job: [jobObject]
    }
}];
