const modelResponse = $('Retrieve links to scrape').first().json.data.modelResponse;
const containsString = $('Unify params').first().json["Links must contain"];
const parsed = JSON.parse(modelResponse);
const links = [...new Set(parsed.internal_links)]; 
const sheetsLinks = $input.all().map(item => item.json.URL)
let response; 

if(containsString === ""){
  response = links
    .map(item =>(
        { json: { link: item.split('?')[0] } }
    ))
} else {
  response = links
    .map(item =>
      item.includes(containsString)
        ? { json: { link: item.split('?')[0] } }
        : null
    )
    .filter(item => item !== null);
}
const dedupeBetweenSheetsAndModel = response.filter(item => !sheetsLinks.includes(item.json.link));
const deduped = [...new Map(dedupeBetweenSheetsAndModel.map(item => [item.json.link, item])).values()]
return deduped
