const data = $("HTTP").all()[0]?.json?.data;

function extractTextFromHTML(html) {
  const cleanedHTML = html
    .replace(/<style[\s\S]*?>[\s\S]*?<\/style>/gi, "")
    .replace(/<[^>]+>/g, "")
    .replace(/\s+/g, " ")
    .trim();

  return cleanedHTML;
}

const cleanedData = extractTextFromHTML(data);

return { cleanedData };

