return items.map(item => {
  const message = $input.first().json.ds_msg.data.message;

  const tp_Mensagem = $input.first().json.ds_msg.data.messageType;

  let objMensagem = {
                      nm_Contato : "",
                      nr_Chave_Contato : "",
                      ds_Mensagem : "",
                      sn_Proprio_Numero : false,   
                      sn_Mensagem_Valida : false,
                      arquivos : []
                    }

  objMensagem.nr_Chave_Contato = $input.first().json.ds_msg.data.key.remoteJid.split('@')[0].split('-')[0]
  objMensagem.sn_Proprio_Numero =  $input.first().json.ds_msg.data.key.fromMe;

  objMensagem.ds_Mensagem =  $input.first().json.ds_msg.data.message.conversation ? $input.first().json.ds_msg.data.message.conversation : $input.first().json.ds_msg.data.message?.reactionMessage?.text;

  objMensagem.sn_Mensagem_Valida = ["conversation", "audioMessage","documentMessage","videoMessage","imageMessage","reactionMessage","documentWithCaptionMessage","stickerMessage","contactMessage"].includes(tp_Mensagem); 

  objMensagem.nm_Contato =  objMensagem.sn_Proprio_Numero ? objMensagem.nr_Chave_Contato : $input.first().json.ds_msg.data.pushName;


  switch($input.first().json.ds_msg.data.messageType)
  {
      case 'audioMessage':
      case 'imageMessage':
      case 'videoMessage':
      case 'documentMessage':
      case 'reactionMessage':
      case 'stickerMessage':
        if (message[tp_Mensagem] && message[tp_Mensagem].url) 
        {   
          objMensagem.ds_Mensagem = message[tp_Mensagem].caption;
          objMensagem.arquivos.push({
            nm_Arquivo: message[tp_Mensagem].fileName || "arquivo",
            tp_Arquivo: message[tp_Mensagem].mimetype || "application/octet-stream",
            ds_Base64: $input.first().json.ds_msg.data.message.base64
          });
        }
      break;

    case "contactMessage":
      objMensagem.ds_Mensagem = Tratar_VCARD($input.first().json.ds_msg.data.message.contactMessage.vcard);
      break;    

    case 'documentWithCaptionMessage':
          objMensagem.ds_Mensagem = $input.first().json.ds_msg.data.message.documentWithCaptionMessage.message.documentMessage.caption;

          objMensagem.arquivos.push({
            nm_Arquivo: $input.first().json.ds_msg.data.message.documentWithCaptionMessage.message.documentMessage.fileName|| "arquivo",
            tp_Arquivo: $input.first().json.ds_msg.data.message.documentWithCaptionMessage.message.documentMessage.mimetype || "application/octet-stream",
            ds_Base64: $input.first().json.ds_msg.data.message.base64
          });
      break;     
  }

  //VERIFICA SE A MENSAGEM VEIO JUNTO COM UM RESPOSTA DE UMA MSG DO WHATSAPP
  if($input.first().json.ds_msg.data.contextInfo?.quotedMessage != null)
  {
    const tipo = Object.keys($input.first().json.ds_msg.data.contextInfo?.quotedMessage)[0];
    var ds_Texto_Citacao = "";

    switch(tipo)
    {
      case 'conversation':
        ds_Texto_Citacao = $input.first().json.ds_msg.data.contextInfo.quotedMessage.conversation;
        break;

      case 'audioMessage':  
      case 'stickerMessage':  
      case 'documentMessage':
      case 'audioMessage':
      case 'imageMessage':        
        //ds_Texto_Citacao = $input.first().json.ds_msg.data.contextInfo.quotedMessage.conversation;

        //PARA FAZER IMAGEM E V√çDEO PRECISA DESCRIPTOGRAR O ARQUIVO USANDO A MEDIA KEY
        break;
    }

    if(ds_Texto_Citacao != "")
    {
      objMensagem.ds_Mensagem = "> "+ ds_Texto_Citacao + "\n\n" + objMensagem.ds_Mensagem;
    }
  }

  return objMensagem;
});



function Tratar_VCARD(ds_Vcard) {
  // Substitui os "\n" por quebras de linha reais
  const vcard = ds_Vcard.replace(/\\n/g, '\n');

  const aLinhas = vcard.split('\n');

  let nm_Nome = '';
  let ds_Telefone = '';

  for (const line of aLinhas) {
    //Nome
    if (line.startsWith('FN:')) {
      nm_Nome = line.replace('FN:', '').trim();
    }

    // WhatsApp Business: telefone + waid
    if (line.includes('waid=')) {
        const waidMatch = line.match(/waid=(\d+):/);
        const formattedMatch = line.match(/waid=\d+:(\+?[0-9\s\-]+)/);

        if (waidMatch) {
           // output.waid = waidMatch[1];
        }

        if (formattedMatch) {
            const phone = formattedMatch[1].replace(/[^\d+]/g, '');
            if (phone) {
                ds_Telefone = phone;
            }
        }
    }

    // WhatsApp normal: apenas TEL sem waid
    if (line.startsWith('TEL') && !line.includes('waid=')) 
    {
      const parts = line.split(':');
      if (parts.length > 1)
      {
        const phone = parts[1].replace(/[^\d+]/g, '');
        if (phone) 
        {
          ds_Telefone = phone;
        }
      }
    }  
  }

  return `üì± Contato compartilhado:\nNome: ${nm_Nome}\nTelefone: ${ds_Telefone}`;
}
