parameters.name:
={{ 
  (() => {
    const date = $json.date.slice(0, 10).replace(/-/g, '');
    const sender = ($json.from?.value?.[0]?.name || 'Unknown').trim();
    const cleanSender = sender
      .replace(/[^a-zA-Z0-9]/g, '_')   // replace non-alphanumeric
      .replace(/_+/g, '_')             // collapse repeated _
      .replace(/^_+|_+$/g, '');        // trim leading/trailing _
    return `${date}_${cleanSender}`;
  })()
}}
