options.customCss:
:root {
  /* Fonts */
  --font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif;
  --font-weight-normal: 400;
  --font-weight-bold: 700;

  /* Font sizes */
  --font-size-body: 14px;
  --font-size-label: 16px;
  --font-size-test-notice: 14px;
  --font-size-input: 16px;
  --font-size-header: 24px;
  --font-size-paragraph: 16px;
  --font-size-link: 14px;
  --font-size-error: 14px;
  --font-size-html-h1: 36px;
  --font-size-html-h2: 28px;
  --font-size-html-h3: 22px;
  --font-size-html-h4: 18px;
  --font-size-html-h5: 16px;
  --font-size-html-h6: 14px;
  --font-size-subheader: 18px;

  /* Colours – Dark Theme */
  --color-background: #191414;             /* Spotify dark background */
  --color-card-bg: #212121;                /* Dark card background */
  --color-card-border: #2a2a2a;
  --color-card-shadow: rgba(0, 0, 0, 0.6);

  --color-header: #FFFFFF;
  --color-label: #EAEAEA;
  --color-input-bg: #212121;               /* ✅ Dark background for inputs */
  --color-input-text: #FFFFFF;             /* ✅ White text */
  --color-input-border: #444444;
  --color-focus-border: #1DB954;

  --color-link: #1DB954;
  --color-link-hover: #1AA34A;
  --color-header-subtext: #B3B3B3;

  --color-submit-btn-bg: #1DB954;
  --color-submit-btn-text: #FFFFFF;
  --color-submit-btn-hover: #1AA34A;

  --color-clear-button-bg: #333333;
  --color-clear-button-text: #FFFFFF;

  --color-test-notice-text: #FFFFFF;
  --color-test-notice-bg: #2a2a2a;
  --color-test-notice-border: #3a3a3a;

  --color-error: #E22134;
  --color-required: #1DB954;

  --color-html-text: #EAEAEA;
  --color-html-link: #1DB954;

  /* Border Radii */
  --border-radius-card: 8px;
  --border-radius-input: 4px;
  --border-radius-clear-btn: 50%;
  --card-border-radius: 8px;

  /* Spacing */
  --padding-container-top: 32px;
  --padding-card: 24px;
  --padding-test-notice-vertical: 16px;
  --padding-test-notice-horizontal: 24px;
  --margin-bottom-card: 24px;
  --padding-form-input: 12px;
  --card-padding: 24px;
  --card-margin-bottom: 24px;

  /* Dimensions */
  --container-width: 480px;
  --submit-btn-height: 48px;
  --checkbox-size: 20px;

  /* Other visuals */
  --box-shadow-card: 0px 4px 16px rgba(0, 0, 0, 0.6);
  --opacity-placeholder: 0.8;
}

/* Inputs */
input, textarea {
  background-color: var(--color-input-bg);
  color: var(--color-input-text);
  border: 1px solid var(--color-input-border);
  border-radius: var(--border-radius-input);
  padding: var(--padding-form-input);
}

input:focus, textarea:focus {
  outline: none;
  border-color: var(--color-focus-border);
  box-shadow: 0 0 0 2px rgba(29, 185, 84, 0.3); /* Spotify green glow */
}

/* Placeholder styling */
::placeholder {
  font-family: system-ui, sans-serif;
  font-size: 0.9em;
  font-weight: 400;
  color: #B3B3B3; /* ✅ More readable grey */
  opacity: 1;
}

