// n8n Code node (JavaScript)
const output = [];

for (const item of items) {
  const data = item.json;

  const firstName = data["data[merges][FNAME]"] || "";
  const lastName = data["data[merges][LNAME]"] || "";

  output.push({
    email: data["data[email]"] || "",
    firstName,
    lastName,
    fullName: `${firstName} ${lastName}`.trim(),
    tags: [],
    listId: data["data[list_id]"] || "",
    subscribedAt: new Date(data["fired_at"]).toISOString()
  });
}

return output.map(o => ({ json: o }));

// Parse OpenAI response and merge with original subscriber data
const item = $input.first();

// Fallback: take current input if node not found
let subscriberData = {};
try {
  subscriberData = $node['Extract Subscriber Data']?.json || item.json;
} catch (e) {
  subscriberData = item.json;
}

let enrichedData = {
  company: 'Unknown Company',
  jobTitle: 'Unknown Role',
  industry: 'Unknown Industry',
  linkedinUrl: '',
  intent: 'General interest',
  leadScore: 50,
  confidence: 30
};

// Try to parse OpenAI response (already JSON)
try {
  const openaiResponse = item.json;
  let content = openaiResponse.output || openaiResponse;

  if (typeof content === 'string') {
    content = content.replace(/```json|```/g, '').trim();
    content = JSON.parse(content);
  }

  enrichedData = { ...enrichedData, ...content };
} catch (error) {
  // Parsing failed – keep defaults
}

const finalData = {
  ...subscriberData,
  ...enrichedData,
  enrichedAt: new Date().toISOString()
};

return { json: finalData };
