const url = $input.first().json.response.contact_info.github
const username = url.split('github.com/')[1].split('/')[0];
return [{ json: { username } }];

const user = items[0].json;

// Parse creation and update dates
const created = new Date(user.created_at);
const updated = new Date(user.updated_at);

// Calculate difference in milliseconds
const diffTime = Math.abs(updated - created);

// Convert milliseconds to years
const years = diffTime / (1000 * 60 * 60 * 24 * 365.25);

// Round to nearest whole number
const experience = `~${Math.round(years)}`;

// Return desired output
return [
  {
    json: {
      login: user.login,
      public_repos: user.public_repos,
      followers: user.followers,
      experience
    }
  }
];

// Initialize counts
let languageCount = {};
let frameworkCount = {};
let totalStars = 0;
let totalForks = 0;

// Full list of frameworks, libraries, and tools
const knownFrameworks = [
  "React", "Angular", "Vue", "Bootstrap", "Tailwind", "jQuery",
  "Node", "Express", "Django", "Flask", "Spring Boot", "Laravel", "Rails",
  "React Native", "Flutter", "Ionic",
  "MongoDB", "MySQL", "PostgreSQL", "Firebase",
  "Docker", "Jenkins", "GitHub", "GitLab", "AWS", "Azure", "Netlify", "Vercel",
  "Jest", "Mocha", "Cypress", "Selenium",
  "FastAPI", "Redux", "Redux Toolkit"
];

// Helper function to extract frameworks
function extractFrameworks(text) {
  if (!text) return [];

  const matches = new Set();
  const lowerText = text.toLowerCase();

  for (const fw of knownFrameworks) {
    if (lowerText.includes(fw.toLowerCase())) {
      matches.add(fw);
    }
  }

  return Array.from(matches);
}

// Loop through repositories
for (const item of items) {
  const repo = item.json;

  // Count languages
  const lang = repo.language;
  if (lang) {
    languageCount[lang] = (languageCount[lang] || 0) + 1;
  }

  // Count frameworks from description and topics
  const descFrameworks = extractFrameworks(repo.description);
  const topicFrameworks = extractFrameworks((repo.topics || []).join(" "));
  const allFrameworks = new Set([...descFrameworks, ...topicFrameworks]);

  for (const fw of allFrameworks) {
    frameworkCount[fw] = (frameworkCount[fw] || 0) + 1;
  }

  // Add stars and forks
  totalStars += repo.stargazers_count || 0;
  totalForks += repo.forks_count || 0;
}

// Return combined result
return [
  {
    json: {
      languageCount,
      frameworkCount,
      totalStars,
      totalForks
    }
  }
];

const allItems = $input.all();

// Defensive checks to ensure indexes exist
const resumeData = allItems[0]?.json?.response || {};
const githubData = allItems[1]?.json || {};
const repoData = allItems[2]?.json || {};

// Merge into one object
const mergedData = {
  ...githubData,
  ...resumeData,
  ...repoData
};

// Return as single item array
return [
  {
    json: mergedData
  }
];
