// Loop over input items and add a new field 'cleanedData' with HTML removed from 'data'
for (const item of $input.all()) {
  const html = item.json.data || '';
  // Remove all HTML tags but keep text content
  const cleaned = html.replace(/<[^>]*>/g, '').trim();
  item.json.cleanedData = cleaned;
}

// Return all items with the new field added
return $input.all();

const rawText = $input.first().json.text || "";

try {
  // Match JSON content between ```json and ```
  const jsonMatch = rawText.match(/```json\s*([\s\S]+?)\s*```/i);

  if (!jsonMatch || !jsonMatch[1]) {
    throw new Error("No valid JSON block found in input.");
  }

  const jsonString = jsonMatch[1].trim();

  // Safely parse JSON
  const parsedJson = JSON.parse(jsonString);

  return [{ json: parsedJson }];
} catch (error) {
  return [{
    json: {
      error: "Failed to parse JSON",
      details: error.message,
    },
  }];
}
