const profile = $input.first().json.output

// Get most recent job
const work = profile.work_experience || [];
const latest = work.length > 0 ? work[0] : {};

function formatDate(dateStr) {
  const date = new Date(dateStr);
  return isNaN(date) ? dateStr : `${date.getFullYear()}-${(date.getMonth() + 1).toString().padStart(2, '0')}`;
}

function getExperienceYears(experiences) {
  if (experiences.length === 0) return '';
  const startDates = experiences.map(e => new Date(e.start_date)).filter(d => !isNaN(d));
  const endDates = experiences.map(e => new Date(e.end_date === 'Present' ? new Date() : e.end_date)).filter(d => !isNaN(d));
  if (startDates.length === 0 || endDates.length === 0) return '';
  const start = new Date(Math.min(...startDates));
  const end = new Date(Math.max(...endDates));
  const diffYears = (end - start) / (1000 * 60 * 60 * 24 * 365);
  return `${Math.floor(diffYears)} years`;
}

function formatEducation(edus) {
  if (!edus || edus.length === 0) return '';
  return edus.map(e => {
    const start = new Date(e.start_year);
    const end = new Date(e.end_year);
    const startYear = isNaN(start) ? '' : start.getFullYear();
    const endYear = isNaN(end) ? '' : end.getFullYear();
    const duration = (startYear && endYear) ? `(${startYear}‚Äì${endYear})` : '';
    return `${e.degree} of ${e.field_of_study} at ${e.institution} ${duration}`;
  }).join(' | ');
}

return [
  {
    json: {
      FullName: profile.full_name,
      JobTitle: profile.job_title,
      Email: profile.contact.email.toLowerCase(),
      Phone: profile.contact.phone,
      Location: profile.contact.address,
      LinkedIn: profile.contact.linkedin,
      Education: formatEducation(profile.education),
      Summary: profile.summary,
      MostRecentCompany: latest.company || '',
      MostRecentPosition: latest.position || '',
      StartDate: formatDate(latest.start_date || ''),
      EndDate: formatDate(latest.end_date || ''),
      Skills: (profile.skills?.technical_skills || []).slice(0, 3).join(', '),
      Experience: getExperienceYears(work), 
      ProfileUrl: $('Get profile detail').first().json.webContentLink
    }
  }
];
