// Array untuk menampung hasil yang akan dikirim ke node selanjutnya
const outputItems = [];

// Loop melalui setiap item yang masuk dari node sebelumnya
for (const item of items) {
  try {
    // 1. Ambil nama perusahaan dari object JSON
    // Pastikan key 'Perusahaan' ada di dalam data Anda
    const namaPerusahaan = item.json.Perusahaan;

    // 2. Lakukan replace spasi dengan '%20'
    // Menggunakan encodeURIComponent() adalah cara yang paling tepat dan aman
    // untuk mempersiapkan string sebagai bagian dari URL.
    // Ini tidak hanya mengganti spasi, tapi juga karakter spesial lainnya.
    const namaPerusahaanEncoded = encodeURIComponent(namaPerusahaan);

    // Alternatif jika Anda HANYA ingin mengganti spasi:
    // const namaPerusahaanEncoded = namaPerusahaan.replace(/ /g, '%20');

    // 3. Buat object baru untuk output
    // Anda bisa menyertakan data asli jika perlu dengan '...item.json'
    const outputData = {
      ...item.json, // Menyalin semua data asli
      perusahaan_url_encoded: namaPerusahaanEncoded // Menambahkan field baru dengan hasil replace
    };

    // Tambahkan hasil ke dalam array output
    outputItems.push({ json: outputData });

  } catch (error) {
    // Jika terjadi error (misalnya key 'Perusahaan' tidak ditemukan),
    // teruskan item tersebut tanpa perubahan untuk menghindari workflow berhenti.
    outputItems.push(item);
  }
}

// Kembalikan semua item yang telah diproses untuk digunakan oleh node selanjutnya
return outputItems;

