parameters.functionCode:
const data = $input.all();
const result = [];

for (const item of data) {
  let normalized = {
    source: item.json.from ? 'email' : 'webhook',
    email: item.json.from?.emailAddress?.address || item.json.email,
    name: item.json.from?.emailAddress?.name || item.json.name,
    subject: item.json.subject || 'Web Request',
    message: item.json.body?.content || item.json.message,
    timestamp: new Date().toISOString()
  };
  result.push({ json: normalized });
}

return result;

const request = $json;
const expertise = request.output.expertise;
const isVIP = request.output.isVIP;
const urgency = request.output.urgency;

// Agent assignment logic
const agents = {
  'frontend': 'john@company.com',
  'backend': 'sarah@company.com',
  'business': 'lisa@company.com',
  'support': 'tom@company.com'
};

const assignedAgent = agents[expertise] || agents['support'];

// SLA calculation
const slaHours = {
  'critical': isVIP ? 1 : 2,
  'high': isVIP ? 4 : 8,
  'medium': isVIP ? 8 : 24,
  'low': 24
};

return [{
  json: {
    ...request,
    assignedAgent,
    slaHours: slaHours[urgency],
    priority: urgency === 'critical' ? 1 : urgency === 'high' ? 2 : 3
  }
}];

