// Cost Analyzer - Process and analyze maintenance costs
const allInputs = $input.all();
const currentDate = new Date().toISOString().split('T')[0];
const analysisResults = [];

// Process each service category
allInputs.forEach((input, index) => {
  const serviceData = input.json;
  let services = [];

  // Extract services from different possible structures
  if (serviceData.result && serviceData.result.services) {
    services = serviceData.result.services;
  } else if (serviceData.result && Array.isArray(serviceData.result)) {
    services = serviceData.result;
  } else if (Array.isArray(serviceData)) {
    services = serviceData;
  }

  // Determine service category based on input index
  const categories = ['Plumbing', 'Electrical', 'HVAC'];
  const category = categories[index] || 'General';

  services.forEach(service => {
    if (service && service.service_name) {
      // Parse price range to get average cost
      let averageCost = 0;
      let minCost = 0;
      let maxCost = 0;

      if (service.price_range) {
        const priceMatch = service.price_range.match(/\$(\d+)[-–](\d+)/);
        if (priceMatch) {
          minCost = parseInt(priceMatch[1]);
          maxCost = parseInt(priceMatch[2]);
          averageCost = (minCost + maxCost) / 2;
        } else {
          const singlePrice = service.price_range.match(/\$(\d+)/);
          if (singlePrice) {
            averageCost = parseInt(singlePrice[1]);
            minCost = maxCost = averageCost;
          }
        }
      }

      // Determine cost level
      let costLevel = 'Medium';
      if (averageCost < 200) costLevel = 'Low';
      else if (averageCost > 500) costLevel = 'High';

      analysisResults.push({
        date_analyzed: currentDate,
        category: category,
        service_name: service.service_name || 'Unknown Service',
        provider_name: service.provider_name || 'Unknown Provider',
        price_range: service.price_range || 'Price not available',
        average_cost: averageCost,
        min_cost: minCost,
        max_cost: maxCost,
        cost_level: costLevel,
        rating: service.rating || 'No rating',
        location: service.location || 'Location not specified',
        phone: service.phone || 'No phone',
        description: service.description || 'No description',
        provider_url: service.provider_url || '#',
        urgency_score: category === 'Plumbing' ? 8 : category === 'Electrical' ? 9 : 6
      });
    }
  });
});

console.log(`Analyzed ${analysisResults.length} services across categories`);

return analysisResults.map(result => ({ json: result }));

// Service Comparer - Compare services and find best options
const services = $input.all().map(item => item.json);
const comparisonResults = [];
const serviceGroups = {};

// Group services by category and service name
services.forEach(service => {
  const key = `${service.category}_${service.service_name}`;
  if (!serviceGroups[key]) {
    serviceGroups[key] = [];
  }
  serviceGroups[key].push(service);
});

// Compare services within each group
Object.keys(serviceGroups).forEach(groupKey => {
  const group = serviceGroups[groupKey];
  if (group.length > 1) {
    // Sort by rating and cost
    const sortedByRating = [...group].sort((a, b) => {
      const ratingA = parseFloat(a.rating) || 0;
      const ratingB = parseFloat(b.rating) || 0;
      return ratingB - ratingA;
    });

    const sortedByCost = [...group].sort((a, b) => a.average_cost - b.average_cost);

    const bestRated = sortedByRating[0];
    const cheapest = sortedByCost[0];
    const mostExpensive = sortedByCost[sortedByCost.length - 1];

    // Calculate price variance
    const costs = group.map(s => s.average_cost).filter(c => c > 0);
    const avgGroupCost = costs.length > 0 ? costs.reduce((a, b) => a + b, 0) / costs.length : 0;
    const priceVariance = costs.length > 1 ? Math.max(...costs) - Math.min(...costs) : 0;

    comparisonResults.push({
      service_category: group[0].category,
      service_name: group[0].service_name,
      provider_count: group.length,
      average_market_price: Math.round(avgGroupCost),
      price_variance: priceVariance,
      best_rated_provider: {
        name: bestRated.provider_name,
        rating: bestRated.rating,
        price: bestRated.price_range,
        phone: bestRated.phone
      },
      cheapest_provider: {
        name: cheapest.provider_name,
        rating: cheapest.rating,
        price: cheapest.price_range,
        phone: cheapest.phone
      },
      most_expensive_provider: {
        name: mostExpensive.provider_name,
        rating: mostExpensive.rating,
        price: mostExpensive.price_range,
        phone: mostExpensive.phone
      },
      recommendation: bestRated.provider_name === cheapest.provider_name ? 
        'Best Value (High Rating + Low Cost)' : 
        parseFloat(bestRated.rating) > 4.5 ? 'Quality Focus' : 'Cost Focus',
      all_providers: group
    });
  } else {
    // Single provider for this service
    const service = group[0];
    comparisonResults.push({
      service_category: service.category,
      service_name: service.service_name,
      provider_count: 1,
      average_market_price: service.average_cost,
      price_variance: 0,
      single_provider: {
        name: service.provider_name,
        rating: service.rating,
        price: service.price_range,
        phone: service.phone
      },
      recommendation: 'Single Option Available',
      all_providers: [service]
    });
  }
});

console.log(`Created comparisons for ${comparisonResults.length} service types`);

return comparisonResults.map(result => ({ json: result }));

// Budget Planner - Create maintenance budget and scheduling recommendations
const comparisons = $input.all().map(item => item.json);
const currentDate = new Date();
const currentMonth = currentDate.getMonth() + 1;
const currentYear = currentDate.getFullYear();

// Define maintenance categories and typical frequencies
const maintenanceSchedule = {
  'HVAC': { frequency: 'biannual', urgency: 'high', typical_cost: 400 },
  'Plumbing': { frequency: 'annual', urgency: 'medium', typical_cost: 300 },
  'Electrical': { frequency: 'biannual', urgency: 'high', typical_cost: 350 }
};

// Create budget recommendations
const budgetPlan = {
  planning_date: currentDate.toISOString().split('T')[0],
  budget_year: currentYear,
  quarterly_budgets: [],
  annual_summary: {
    total_estimated_cost: 0,
    high_priority_services: [],
    medium_priority_services: [],
    low_priority_services: []
  },
  service_recommendations: [],
  cost_optimization_tips: []
};

// Process each service comparison
comparisons.forEach(comparison => {
  const category = comparison.service_category;
  const schedule = maintenanceSchedule[category] || { frequency: 'annual', urgency: 'medium', typical_cost: 250 };

  // Determine priority based on service type and cost
  let priority = 'Medium';
  if (category === 'Electrical' || category === 'HVAC') priority = 'High';
  if (comparison.average_market_price > 500) priority = 'High';
  if (comparison.average_market_price < 200) priority = 'Low';

  const recommendation = {
    service: comparison.service_name,
    category: category,
    priority: priority,
    estimated_annual_cost: comparison.average_market_price,
    recommended_frequency: schedule.frequency,
    best_provider: comparison.best_rated_provider || comparison.single_provider,
    budget_provider: comparison.cheapest_provider || comparison.single_provider,
    next_service_month: schedule.frequency === 'biannual' ? 
      [3, 9] : schedule.frequency === 'quarterly' ? [3, 6, 9, 12] : [6],
    cost_savings_potential: comparison.price_variance || 0
  };

  budgetPlan.service_recommendations.push(recommendation);
  budgetPlan.annual_summary.total_estimated_cost += comparison.average_market_price;

  // Categorize by priority
  if (priority === 'High') {
    budgetPlan.annual_summary.high_priority_services.push(recommendation);
  } else if (priority === 'Medium') {
    budgetPlan.annual_summary.medium_priority_services.push(recommendation);
  } else {
    budgetPlan.annual_summary.low_priority_services.push(recommendation);
  }
});

// Create quarterly budget breakdown
for (let quarter = 1; quarter <= 4; quarter++) {
  const quarterMonths = [(quarter - 1) * 3 + 1, (quarter - 1) * 3 + 2, (quarter - 1) * 3 + 3];
  let quarterBudget = 0;
  const quarterServices = [];

  budgetPlan.service_recommendations.forEach(rec => {
    rec.next_service_month.forEach(month => {
      if (quarterMonths.includes(month)) {
        quarterBudget += rec.estimated_annual_cost;
        quarterServices.push({
          service: rec.service,
          month: month,
          cost: rec.estimated_annual_cost,
          provider: rec.best_provider.name
        });
      }
    });
  });

  budgetPlan.quarterly_budgets.push({
    quarter: quarter,
    months: quarterMonths,
    total_budget: quarterBudget,
    services: quarterServices
  });
}

// Add cost optimization tips
budgetPlan.cost_optimization_tips = [
  'Bundle multiple services with the same provider for discounts',
  'Schedule preventive maintenance to avoid emergency repairs',
  'Get multiple quotes for expensive services (>$500)',
  'Consider seasonal pricing - HVAC services may be cheaper in off-season',
  'Join service contracts for regular maintenance at reduced rates'
];

// Calculate budget metrics
budgetPlan.annual_summary.monthly_average = Math.round(budgetPlan.annual_summary.total_estimated_cost / 12);
budgetPlan.annual_summary.high_priority_cost = budgetPlan.annual_summary.high_priority_services
  .reduce((sum, service) => sum + service.estimated_annual_cost, 0);
budgetPlan.annual_summary.potential_savings = budgetPlan.service_recommendations
  .reduce((sum, rec) => sum + rec.cost_savings_potential, 0);

console.log(`Created budget plan with total estimated cost: $${budgetPlan.annual_summary.total_estimated_cost}`);

return [{ json: budgetPlan }];

// Property Manager Alert - Format comprehensive maintenance report
const budgetData = $input.all()[0].json;
const currentDate = new Date().toLocaleDateString();
const currentTime = new Date().toLocaleTimeString();

// Create formatted alert message
function formatCurrency(amount) {
  return new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    minimumFractionDigits: 0
  }).format(amount);
}

function createMaintenanceAlert() {
  const { annual_summary, quarterly_budgets, service_recommendations } = budgetData;

  let alertMessage = `🏠 **PROPERTY MAINTENANCE COST ANALYSIS**\n`;
  alertMessage += `📅 Generated: ${currentDate} at ${currentTime}\n\n`;

  // Annual Summary
  alertMessage += `💰 **ANNUAL BUDGET SUMMARY**\n`;
  alertMessage += `• Total Estimated Cost: ${formatCurrency(annual_summary.total_estimated_cost)}\n`;
  alertMessage += `• Monthly Average: ${formatCurrency(annual_summary.monthly_average)}\n`;
  alertMessage += `• Potential Savings: ${formatCurrency(annual_summary.potential_savings)}\n\n`;

  // Priority Breakdown
  alertMessage += `🚨 **SERVICE PRIORITIES**\n`;
  alertMessage += `• High Priority: ${annual_summary.high_priority_services.length} services (${formatCurrency(annual_summary.high_priority_cost)})\n`;
  alertMessage += `• Medium Priority: ${annual_summary.medium_priority_services.length} services\n`;
  alertMessage += `• Low Priority: ${annual_summary.low_priority_services.length} services\n\n`;

  // Quarterly Breakdown
  alertMessage += `📊 **QUARTERLY BUDGET BREAKDOWN**\n`;
  quarterly_budgets.forEach(quarter => {
    if (quarter.total_budget > 0) {
      alertMessage += `Q${quarter.quarter}: ${formatCurrency(quarter.total_budget)} (${quarter.services.length} services)\n`;
    }
  });
  alertMessage += `\n`;

  // Top Service Recommendations
  alertMessage += `🔧 **TOP SERVICE RECOMMENDATIONS**\n`;
  const topServices = service_recommendations
    .filter(rec => rec.priority === 'High')
    .slice(0, 5);

  topServices.forEach((service, index) => {
    alertMessage += `${index + 1}. **${service.service}** (${service.category})\n`;
    alertMessage += `   • Cost: ${formatCurrency(service.estimated_annual_cost)}\n`;
    alertMessage += `   • Provider: ${service.best_provider.name}\n`;
    alertMessage += `   • Rating: ${service.best_provider.rating}\n`;
    alertMessage += `   • Phone: ${service.best_provider.phone}\n\n`;
  });

  // Cost Optimization Tips
  alertMessage += `💡 **COST OPTIMIZATION TIPS**\n`;
  budgetData.cost_optimization_tips.slice(0, 3).forEach((tip, index) => {
    alertMessage += `${index + 1}. ${tip}\n`;
  });

  alertMessage += `\n━━━━━━━━━━━━━━━━━━━━━━\n`;
  alertMessage += `📈 **Next Update**: Next week\n`;
  alertMessage += `🔄 **Data Source**: Multiple contractor websites\n`;

  return alertMessage;
}

// Create summary for property management system
function createSystemSummary() {
  return {
    alert_type: 'maintenance_cost_analysis',
    severity: 'info',
    property_count: 1, // Adjust based on actual properties
    total_annual_budget: budgetData.annual_summary.total_estimated_cost,
    high_priority_count: budgetData.annual_summary.high_priority_services.length,
    next_quarter_budget: budgetData.quarterly_budgets[0]?.total_budget || 0,
    top_expense_category: budgetData.service_recommendations
      .reduce((max, current) => 
        current.estimated_annual_cost > max.estimated_annual_cost ? current : max
      ).category,
    generated_at: new Date().toISOString(),
    recommendations_count: budgetData.service_recommendations.length
  };
}

const alertMessage = createMaintenanceAlert();
const systemSummary = createSystemSummary();

console.log(`Generated property manager alert with ${budgetData.service_recommendations.length} service recommendations`);

return [
  {
    json: {
      message_text: alertMessage,
      system_summary: systemSummary,
      full_budget_data: budgetData,
      alert_timestamp: new Date().toISOString(),
      requires_action: budgetData.annual_summary.high_priority_services.length > 0
    }
  }
];
