parameters.jsCode:
return $input.all().flatMap(item => {
  // Check if 'place_results' exists on the item.json object
  const placeResults = item.json.place_results;

  if (!placeResults) {
    console.log("No 'place_results' found for item.");
    return [];
  }

  const restaurant = placeResults.title || "Unknown";
  const reviews = placeResults.user_reviews?.most_relevant; // Accessing the correct path for reviews

  // Check if 'reviews' is an array
  if (!Array.isArray(reviews)) {
    console.log("No 'most_relevant' reviews array found for:", restaurant);
    return [];
  }

  return reviews.slice(0, 10).map(review => ({
    json: {
      restaurant,
      reviewText: review.description || "", // Changed from review.snippet to review.description
      stars: review.rating || null,
      postedAt: review.date || ""
    }
  }));
});
