// Mengambil teks dari input menggunakan sintaks yang Anda tentukan.
const fullText = $input.first().json.data

// Regex untuk mengekstrak blok thread.
const regex = /\[THREADSTART\]([\s\S]*?)\[THREADEND\]/;
const match = fullText.match(regex);

// Variabel untuk menampung hasil akhir.
let finalArray = [];

// Jika blok thread ditemukan, proses isinya.
if (match && match[1]) {
  const contentBlock = match[1];

  // Pisahkan string menjadi array, lalu bersihkan setiap elemen.
  finalArray = contentBlock
    .split('-cutthread-')
    .map(thread => thread.trim())
    .filter(thread => thread.length > 0);
}

// Kembalikan satu item baru dengan format yang Anda inginkan.
// Stuktur { json: { ... } } adalah format standar n8n untuk item.
return [{
  json: {
    data: finalArray
  }
}];
