let obj = {};

for (const item of $input.all()) {
  const platform = item.json.Platform;
  const min = item.json.Min_Limit;
  const max = item.json.Max_Limit;

  obj[platform] = {
    platform,
    min: min,
    max: max
  };
}

// Return the object inside an array with `json` key (required by n8n)
return [
  {
    json: obj
  }
];

