const provider = $json["Provider vs."];
const slug = provider.toLowerCase().replace(/\s+/g, "-");

const safe = (v) => v || "";

return [{
  json: {
    name: provider,
    slug,
    htmlContent: `
      <h1>${safe(provider)}</h1>
      <section>${safe($json.Intro)}</section>
      <section>${safe($json["Comparison Table"])}</section>
      <section>${safe($json["eSIM Activation"])}</section>
      <section>${safe($json["User Ratings"])}</section>
      <section>${safe($json.FAQs)}</section>
    `
  }
}];

// Extract product names from the "All Products" column
const productNames = $input.all().map(item => item.json["All Products"]).filter(Boolean);

const pairs = [];

for (let i = 0; i < productNames.length; i++) {
  for (let j = i + 1; j < productNames.length; j++) {
    const name = `${productNames[i]} vs ${productNames[j]}`;
    const slug = name.toLowerCase().replace(/[^a-z0-9]+/g, '-').replace(/^-|-$/g, '');
    pairs.push({
      json: {
        name,
        slug,
        product1: productNames[i],
        product2: productNames[j]
      }
    });
  }
}

return pairs;
