// Works in n8n Code node
// Filters only articles that are between 1 and 2 days old

function parseRelative(relative, baseNow) {
  if (typeof relative !== 'string') return null;
  const s = relative.trim().toLowerCase();

  // quick rejects
  if (s.includes('yesterday')) {
    // treat "yesterday" as exactly 1 day ago
    return new Date(baseNow.getTime() - 24 * 3600000);
  }
  if (s.includes('week') || s.includes('month') || s.includes('year')) return null;

  // match "15 hours ago", "2 days ago", etc.
  const m = s.match(/(\d+)\s*(second|seconds|sec|secs|minute|min|minutes|mins|hour|hours|day|days)\s*ago$/);
  if (!m) return null;

  const amount = parseInt(m[1], 10);
  const unit = m[2];

  const msMap = {
    second: 1000, seconds: 1000, sec: 1000, secs: 1000,
    minute: 60000, minutes: 60000, min: 60000, mins: 60000,
    hour: 3600000, hours: 3600000,
    day: 86400000, days: 86400000,
  };

  const ms = msMap[unit];
  if (!ms) return null;

  return new Date(baseNow.getTime() - amount * ms);
}

// Collect articles from either shape
let articles = [];
if (items.length === 1 && Array.isArray(items[0].json)) {
  articles = items[0].json;
} else {
  articles = items.map(i => i.json);
}

// Determine "now" (or override with present_date if provided)
let baseNow = new Date();
const present = items?.[0]?.json?.present_date;
if (typeof present === 'string' && /^\d{4}-\d{2}-\d{2}$/.test(present)) {
  const [y, m, d] = present.split('-').map(n => parseInt(n, 10));
  baseNow = new Date(y, m - 1, d, 12, 0, 0, 0);
}

// Define boundaries
const twoDaysAgo = new Date(baseNow.getTime() - 2 * 24 * 3600000);
const oneDayAgo = new Date(baseNow.getTime() - 1 * 24 * 3600000);

const out = [];
for (const a of articles) {
  const rel = a.date || a.published_time || a.publishedAt || a.pubDate || a.time || '';
  const abs = parseRelative(rel, baseNow);
  if (!abs) continue;

  // Keep only if between 1 and 2 days ago
  if (abs >= twoDaysAgo && abs < oneDayAgo) {
    const url = a.link || a.url || a.uri;
    if (!url) continue;

    out.push({ json: { URL: String(url), score: 10 } });
  }
}

return out;

// n8n Code node, JavaScript

// If the incoming item is a single item whose .json is an array,
// make it our working array. Otherwise assume multiple items.
const raw = Array.isArray(items[0]?.json) ? items[0].json : items.map(i => i.json);

function cleanMarkdown(md) {
  if (!md || typeof md !== "string") return "";

  let text = md;

  // Remove markdown images ![alt](url) and bare images ![](url)
  text = text.replace(/!\[[^\]]*?\]\([^)]+\)/g, "");

  // Replace markdown links [text](url) with just "text"
  text = text.replace(/\[([^\]]+)\]\((?:https?:\/\/|mailto:)[^)]+\)/g, "$1");

  // Remove bare URLs
  text = text.replace(/https?:\/\/\S+/g, "");

  // Cut everything after non article sections
  const cutMarkers = [
    /^##\s*More videos\b/mi,
    /^##\s*Related Articles\b/mi,
    /^##\s*More Regional News\b/mi,
    /^\s*Share This Article\b/mi,
    /^##\s*About Me\b/mi,
  ];
  for (const rx of cutMarkers) {
    const idx = text.search(rx);
    if (idx !== -1) {
      text = text.slice(0, idx);
      break;
    }
  }

  // Drop common clutter lines
  const dropStarts = [
    "Skip to content",
    "Skip to main content",
    "Share",
    "Watch later",
    "Copy link",
    "Include playlist",
    "More videos",
    "You are signed out",
    "CancelConfirm",
    "Search",
    "Shopping",
    "Info",
    "Tap to unmute",
    "If playback does not begin",
    "Video Player is loading",
    "Play Video",
    "Stream Type",
    "Seek to live",
    "Remaining Time",
    "Playback Rate",
    "Chapters",
    "Captions",
    "Audio Track",
    "Fullscreen",
    "Close Modal Dialog",
    "Topic:",
  ];
  text = text
    .split("\n")
    .filter(line => {
      const L = line.trim();
      if (!L) return true;
      return !dropStarts.some(s => L.startsWith(s));
    })
    .join("\n");

  // Remove heading markers but keep their words
  text = text.replace(/^#{1,6}\s*/gm, "");

  // Remove blockquote markers
  text = text.replace(/^\s*>\s?/gm, "");

  // Remove leftover image captions such as "(Image by ...)"
  text = text.replace(/\((?:Image by|Photo|GIF|Video)[^)]+\)\s*/gi, "");

  // Compress extra blank lines
  text = text.replace(/\n{3,}/g, "\n\n").trim();

  return text;
}

const out = raw.map(a => {
  const title = a.title || "";
  const content = a.content || "";
  return {
    json: {
      title,
      article: cleanMarkdown(content),
    },
  };
});

return out;
