const fromEmail = $input.first().json.From // Assuming 'from' field holds the full "From" string
let name = '';

// Check if the name is enclosed in angle brackets
if (fromEmail.includes('<') && fromEmail.includes('>')) {
    name = fromEmail.substring(0, fromEmail.indexOf('<')).trim();
} else {
    // If no angle brackets, assume the whole "From" field is the name
    name = fromEmail.trim();
}

return { json: { name: name ,Review : $input.first().json.snippet} };

