// Get the AI Agent output (which is a JSON string)
const msg = $input.first().json.output || "";

if (!msg) {
  throw new Error("AI Agent output not found.");
}

// Parse the JSON string
let data;
try {
  data = JSON.parse(msg);
} catch (err) {
  throw new Error("Failed to parse AI Agent output as JSON: " + err.message);
}

// Fallback Appointment ID if missing
if (!data.appointment_id || data.appointment_id === "null") {
  const randomId = Math.random().toString(36).substring(2, 8).toUpperCase();
  data.appointment_id = `APT-${randomId}`;
}

return [{ json: data }];

