const result = {}

function summarizeConversation(messages) {
  return messages.map(msg => {
    const date = new Date(Number(msg.internalDate)).toISOString();
    return `

📅 ${date}
📨 From: ${msg.From}
📥 To: ${msg.To}
📌 Subject: ${msg.Subject}

${msg.snippet}
`.trim();
  }).join("\n\n-----------------------\n\n");
}

$json.emailSummary = summarizeConversation($json.messages);

return $json;
