function removeHtmlCssScript(text) {
  if (!text || typeof text !== 'string') {
    return '';
  }

  let cleanText = text;

  // Remove script tags and their content (case insensitive)
  cleanText = cleanText.replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '');

  // Remove style tags and their content (case insensitive)
  cleanText = cleanText.replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '');

  // Remove HTML comments
  cleanText = cleanText.replace(/<!--[\s\S]*?-->/g, '');

  // Remove all remaining HTML tags
  cleanText = cleanText.replace(/<[^>]*>/g, '');

  // Decode common HTML entities
  const htmlEntities = {
    '&amp;': '&',
    '&lt;': '<',
    '&gt;': '>',
    '&quot;': '"',
    '&#39;': "'",
    '&nbsp;': ' ',
    '&copy;': '¬©',
    '&reg;': '¬Æ',
    '&trade;': '‚Ñ¢'
  };

  Object.keys(htmlEntities).forEach(entity => {
    const regex = new RegExp(entity, 'g');
    cleanText = cleanText.replace(regex, htmlEntities[entity]);
  });

  // Clean up extra whitespace
  cleanText = cleanText.replace(/\s+/g, ' ').trim();

  return cleanText;
}

