// Inputs: array of items coming from "Message a model"
// Each item usually looks like { json: { message: { role: 'assistant', content: '...' }, ... } }

const items = $input.all();

// Helper: pick a grouping key per profile if present
const keyOf = (j) =>
  j.profileUrl ||
  j.author?.profile_url ||
  j.author?.username ||
  j.username ||
  'Feed';

// Build buckets per profile
const buckets = new Map();
for (const it of items) {
  const j = it.json ?? {};
  const key = keyOf(j);
  const arr = buckets.get(key) || [];
  arr.push(j);
  buckets.set(key, arr);
}

// Compose markdown
const today = new Date().toISOString().slice(0, 10);
let md = `# LinkedIn Digest (${today})\n\n`;

for (const [profile, arr] of buckets.entries()) {
  md += `## ${profile}\n\n`;
  arr.forEach((j, idx) => {
    const content =
      j.message?.content ?? j.content ?? j.text ?? '(no content)';
    const url =
      j.url || j.post?.url || j.source_url || j.link || null;

    md += `**${idx + 1}.** ${content}\n`;
    if (url) md += `\n[Link](${url})\n`;
    md += `\n---\n`;
  });
  md += `\n`;
}

// Slack hard limit ~4000 chars for a single text message.
// Trim if needed to be safe.
const MAX = 3800;
if (md.length > MAX) {
  md = md.slice(0, MAX - 50) + '\n\n…_truncated_';
}

// Return ONE item that downstream nodes can use.
// Slack node: set Message Text = {{ $json.text }}  (or {{ $json.markdown }})
return [
  {
    json: {
      markdown: md,
      text: md,
    },
  },
];

const posts = items.map(item => item.json);

// find min and max date in this batch
const dates = posts.map(p => new Date(p.posted_at?.date));
const minDate = new Date(Math.min(...dates));
const maxDate = new Date(Math.max(...dates));

function formatDate(d) {
  return d.toISOString().split("T")[0]; // YYYY-MM-DD
}

// group by author username
const grouped = {};
for (const post of posts) {
  const author = `${post.author?.first_name || ""} ${post.author?.last_name || ""}`.trim() || "Unknown";
  if (!grouped[author]) grouped[author] = [];
  grouped[author].push(post);
}

let digest = `## LinkedIn Digest (${formatDate(minDate)} → ${formatDate(maxDate)})\n\n`;

for (const [author, authorPosts] of Object.entries(grouped)) {
  digest += `### ${author}\n`;
  for (const post of authorPosts) {
    const date = post.posted_at?.date || "Unknown date";
    const text = (post.text || "").slice(0, 200).replace(/\n+/g, " "); // keep short snippet
    const reactions = post.stats?.total_reactions || 0;
    const comments = post.stats?.comments || 0;
    const reposts = post.stats?.reposts || 0;
    digest += `- (${date}) "${text}..."\n  👍 ${reactions} | 💬 ${comments} | 🔁 ${reposts} | [Link](${post.url})\n`;
  }
  digest += "\n";
}

return [{ json: { digest } }];

// Input: one item with json.markdown (or json.text)
// Output: N items -> { text, part, total } for Slack

const MAX = 35000; // safe margin under Slack's ~40k limit
const SEP = '\n---\n';

const md = $input.first().json.markdown ?? $input.first().json.text ?? '';
if (!md) return [{ json: { text: '(empty digest)' } }];

// Split on section separators, then pack sections into <= MAX chunks
const sections = md.split(SEP);
const chunks = [];
let buf = '';

function pushBuf() {
  if (buf.trim()) chunks.push(buf.trim());
  buf = '';
}

for (const s of sections) {
  const candidate = buf ? `${buf}${SEP}${s}` : s;
  if (candidate.length <= MAX) {
    buf = candidate;
  } else {
    // flush what we have
    pushBuf();
    // if a single section is still too long, split it on newlines
    if (s.length > MAX) {
      let start = 0;
      while (start < s.length) {
        let end = Math.min(start + MAX, s.length);
        const nl = s.lastIndexOf('\n', end);
        if (nl > start + 1000) end = nl; // try not to cut mid‑line
        chunks.push(s.slice(start, end).trim());
        start = end;
      }
    } else {
      buf = s; // start new buffer with this section
    }
  }
}
pushBuf();

return chunks.map((c, i) => ({
  json: { text: c, part: i + 1, total: chunks.length }
}));

// --- CONFIG ---
// Change this to the exact node name that has the LinkedIn objects:
const SOURCE_NODE = 'Apify: Start Scraper';   // e.g. 'API Request' if that’s your node’s name

// --- READ ITEMS SAFELY ---
const items = $(SOURCE_NODE).all(); // requires “Run Once for All Items”

// Your API payload shows each item has a .url field.
// If sometimes it’s nested, add more fallbacks in the mapper.
const links = items
  .map(i => i.json?.url || i.json?.article?.url || i.json?.reshared_post?.url)
  .filter(Boolean);

// Build Slack-friendly list
const linksText = '*Sources for today’s digest:*\n' +
  links.map((u, i) => `${i + 1}. <${u}|Post ${i + 1}>`).join('\n');

// Output a single item for the Slack Threads node
return [{ json: { linksText } }];

return items.map(item => {
  let digest = item.json.digest;

  // Remove markdown headers
  digest = digest.replace(/^#+\s?/gm, "");

  // Replace markdown links [text](url) → just "text"
  digest = digest.replace(/\[([^\]]+)\]\([^)]+\)/g, "$1");

  // Remove "(Link: …)" remnants
  digest = digest.replace(/\(Link:[^)]+\)/g, "");

  // Unescape quotes \" → "
  digest = digest.replace(/\\"/g, '"');

  // Convert \n to real newlines
  digest = digest.replace(/\\n/g, "\n");

  // Collapse multiple newlines
  digest = digest.replace(/\n{2,}/g, "\n");

  // Trim leading/trailing spaces
  digest = digest.trim();

  return {
    json: {
      text: digest
    }
  };
});

return items.map(item => {
  const text = item.json.text;
  const match = text.match(/LinkedIn Digest \((.*?)\)/);
  return {
    json: {
      date: match ? match[1] : new Date().toISOString().split("T")[0],
      text: text.replace(/LinkedIn Digest.*?\n/, "") // strip old header
    }
  }
});
