// Loop over input items and extract meeting ID from the URL
for (const item of $input.all()) {
 const url = item.json.output.meeting_notes;

 // Find the position of "::" and "?"
 const startIndex = url.indexOf("::") + 2;
 const endIndex = url.indexOf("?");

 // Extract the meeting ID
 const meetingId = url.substring(startIndex, endIndex);

 // Replace the entire item with the desired structure
 item.json = { 
   body: { 
     meetingId: meetingId 
   } 
 };
}
return $input.all();

// Accedi all'array sentences dal primo item
const transcriptArray = $input.first().json.sentences;

// Verifica che l'array esista
if (!transcriptArray || !Array.isArray(transcriptArray)) {
  throw new Error('Array sentences non trovato o non √® un array');
}

// Crea la trascrizione combinata
let fullTranscript = '';

transcriptArray.forEach((segment) => {
  // Usa text_cleanup se disponibile, altrimenti fallback su text o raw_text
  const text = segment.ai_filters?.text_cleanup || segment.text || segment.raw_text;
  const speakerName = segment.speaker_name || 'Unknown Speaker';

  // Aggiungi il segmento alla trascrizione completa
  fullTranscript += `${speakerName}: ${text}\n`;
});

// Rimuovi l'ultimo \n se presente
fullTranscript = fullTranscript.trim();

// Loop over input items e aggiungi il campo full_transcript
for (const item of $input.all()) {
  item.json.full_transcript = fullTranscript;
  item.json.total_segments = transcriptArray.length;
}

//return $input.all();
// Restituisci solo i campi richiesti
return [{
  json: {
    full_transcript: fullTranscript,
    total_segments: transcriptArray.length
  }
}];
