// Get text from previous node (adjust field if needed)
const input = $input.first().json.output.summary_text;

// Step 1: Clean text (normalize line breaks)
let text = input.replace(/\r\n/g, "\n");

// Step 2: Parse into plain text + style spans
let plainText = "";
let styles = [];
let bullets = [];
let cursor = 1;

const lines = text.split("\n");

for (let line of lines) {
  if (!line.trim()) {
    plainText += "\n";
    cursor++;
    continue;
  }

  let isBullet = false;
  if (line.trim().startsWith("‚ú¶") || line.trim().startsWith("‚úì") || line.trim().startsWith("-")) {
    isBullet = true;
    line = line.trim().substring(1).trim();
  }

  const regex = /(\*\*([^*]+)\*\*|\*([^*]+)\*|([^*]+))/g;
  let match;
  while ((match = regex.exec(line)) !== null) {
    let raw = match[0];
    let clean = match[2] || match[3] || match[4] || "";
    const start = cursor;
    const end = cursor + clean.length;

    plainText += clean;

    if (raw.startsWith("**")) {
      styles.push({
        range: { startIndex: start, endIndex: end },
        textStyle: { bold: true },
        fields: "bold"
      });
    } else if (raw.startsWith("*")) {
      styles.push({
        range: { startIndex: start, endIndex: end },
        textStyle: { italic: true },
        fields: "italic"
      });
    }

    cursor = end;
  }

  plainText += "\n";
  cursor++;

  if (isBullet) {
    bullets.push({
      range: {
        startIndex: cursor - line.length - 1,
        endIndex: cursor
      },
      bulletPreset: "BULLET_DISC_CIRCLE_SQUARE"
    });
  }
}

// Step 3: Build requests
let requests = [];

// Insert everything at once
requests.push({
  insertText: {
    endOfSegmentLocation: {},
    text: plainText
  }
});

// Add styles
for (let s of styles) {
  requests.push({
    updateTextStyle: {
      range: s.range,
      textStyle: s.textStyle,
      fields: s.fields
    }
  });
}

// Add bullets
for (let b of bullets) {
  requests.push({
    createParagraphBullets: b
  });
}

return [
  {
    json: { requests }
  }
];
