const items = $input.all(); // get all items
const paymentMethods = items.map(i => i.json); // extract JSON from each

const hasCompanyCredit = paymentMethods.some(method => method.code === 'companycredit');

return [{
  json: {
    hasCompanyCredit
  }
}];

const addresses = $json.customer.addresses || [];

const defaultAddress = addresses.find(addr => addr.default_shipping === true);

return [{
  json: {
    ...defaultAddress
  }
}];

const results = [];

for (const item of items) {
  for (const key in item.binary) {
    if (
      Object.prototype.hasOwnProperty.call(item.binary, key) &&
      (item.binary[key].mimeType === 'application/pdf' || (item.binary[key].mimeType === 'application/octet-stream' && item.binary[key].fileExtension === 'pdf') )
    ) {
      results.push({
        json: {
          attachmentName: key
        },
        binary: {
          ["attachment"]: item.binary[key]
        }
      });
    }
  }
}

return results;

const content = $json.content;
const emails = $input.first().json.content.email ?? [];
const filteredEmails = emails.filter(email => !email.toLowerCase().includes('@example.com'));
const updatedContent = {
    ...content,
    email: filteredEmails
};

return [{
    json: {
        content: updatedContent
    }
}];

return items.map(item => {
  // veilige clone (ook van json) zodat we het origineel niet muteren
  const newItem = { ...item, json: { ...item.json } };

  const content = newItem.json.content || {};
  const srcProducts = Array.isArray(content.products) ? content.products : [];

  // bewerk elke productregel
  const newProducts = srcProducts.map(p => {
    const product = { ...p };
    let num = product.articlenumber;

    if (typeof num === 'string') {
      // trim en case-insensitive check
      const trimmed = num.trim();
      if (trimmed.toUpperCase().startsWith('SCH')) {
        // verwijder alleen de eerste 3 tekens (de 'SCH' prefix)
        product.articlenumber = trimmed.substring(3);
      } else {
        product.articlenumber = trimmed; // normaliseer zonder prefix
      }
    }

    return product;
  });

  // zet terug in content.products
  newItem.json.content = { ...content, products: newProducts };

  return newItem;
});

