    warningText = '‚úÖ Your balance is sufficient for transactions.';
  }

  const message = `üí∞ *Balance Information*\n\nüí≥ Deposit Balance: *Rp ${balance.toLocaleString('id-ID')}*\n\n${warningText}\n\nüìä Status: ${statusIcon} ${statusColor}\n\n‚è∞ Checked at: ${new Date().toLocaleString('en-US', { timeZone: 'Asia/Jakarta' })} WIB`;

