  if (response.json && response.json.data && response.json.data.rc) {
    const rc = response.json.data.rc;
    const msg = response.json.data.message || 'Unknown error';
    errorMessage += `üìã Response Code: ${rc}\nüí¨ Message: ${msg}\n\n`;

    switch (rc) {
      case '41':
        errorMessage += 'üí° Solution: Invalid signature, check API Key';
        break;
      case '45':
        errorMessage += 'üí° Solution: IP not recognized, contact admin';
        break;
      default:
        errorMessage += 'üí° Contact customer service for assistance';
    }
  } else {
    errorMessage += 'üîç Network error occurred\nüí° Please try again in a few moments';
  }

  return {
    json: {
      success: false,
      message: errorMessage
    }
  };
}

