// Format product list response with categorization and pagination
const response = $input.first();

if (response.json && response.json.data) {
  const products = response.json.data;

  // Group products by category
  const categories = {};
  products.forEach(product => {
    const category = product.category || 'Others';
    if (!categories[category]) {
      categories[category] = [];
    }
    categories[category].push(product);
  });

  // Create menu for categories
  let message = 'üõçÔ∏è *Product Category List*\n\n';
  const buttons = [];
  const buttonRow = [];

  Object.keys(categories).forEach((category, index) => {
    const count = categories[category].length;
    message += `${index + 1}. ${category} (${count} products)\n`;

    buttonRow.push({
      text: `${category} (${count})`,
      callbackData: `category_${category.replace(/\s+/g, '_').toLowerCase()}`
    });

    // Add row every 2 buttons
    if (buttonRow.length === 2) {
      buttons.push([...buttonRow]);
      buttonRow.length = 0;
    }
  });

  // Add remaining button if any
  if (buttonRow.length > 0) {
    buttons.push(buttonRow);
  }

  // Add navigation buttons
  buttons.push([{
    text: 'üîô Main Menu',
    callbackData: 'main_menu'
  }]);

  return {
    json: {
      success: true,
      message: message,
      buttons: buttons,
      categories: categories
    }
  };
} else {
  return {
    json: {
      success: false,
      message: '‚ùå *Failed to Load Product List*\n\nüîç An error occurred while retrieving product data\nüí° Please try again in a few moments',
      buttons: [[{
        text: 'üîÑ Try Again',
        callbackData: 'list_products'
      }, {
        text: 'üîô Main Menu',
        callbackData: 'main_menu'
      }]]
    }
  };
}

