// Enhanced input validation and session management
const messageText = $('Telegram Trigger').item.json.message?.text;
const userId = $('Telegram Trigger').item.json.message?.from?.id;

// Validation functions
function validatePhoneNumber(phone) {
  const phoneRegex = /^(\+62|62|0)([0-9]{9,12})$/;
  return phoneRegex.test(phone.replace(/[-\s]/g, ''));
}

function validatePLNNumber(pln) {
  const plnRegex = /^[0-9]{11,12}$/;
  return plnRegex.test(pln);
}

function validateAmount(amount) {
  const num = parseFloat(amount.replace(/[^\d]/g, ''));
  return num >= 10000 && num <= 10000000; // 10k - 10M
}

// Session state management (simple in-memory for demo)
// In production, use Redis or database
const userSessions = global.userSessions || (global.userSessions = {});

return {
  json: {
    validation: {
      validatePhoneNumber,
      validatePLNNumber,
      validateAmount
    },
    session: userSessions[userId] || {},
    userId: userId,
    messageText: messageText
  }
};

