={{ (function(){
  const title = $json.title || '';
  const content = $json['content'] || $json['content:encodedSnippet'] || $json.description || '';
  const fullText = title + (content ? '\n\n' + content : '');
  const p = $json.platform;
  let m = 500;
  if(p==='X_TWITTER') m=280;
  if(p==='BLUE_SKY') m=300;
  if(fullText.length <= m) return fullText;
  const d = fullText.lastIndexOf('.', m), e = fullText.lastIndexOf('!', m), q = fullText.lastIndexOf('?', m);
  const s = Math.max(d, e, q), l = m*0.6;
  if(s > 0 && s >= l) return fullText.substring(0, s+1);
  const w = fullText.lastIndexOf(' ', m);
  return fullText.substring(0, w>0?w:m) + '...';
})() }}

