const input = $input.all();
const formData = input[0].json;

const name = formData["Please enter your name"];
const mobile = String(formData["Please enter your phone number"]);

function generateCustomerId() {
  const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
  let id = '';
  for (let i = 0; i < 6; i++) {
    id += chars.charAt(Math.floor(Math.random() * chars.length));
  }
  return `CUST-${id}`;
}

const customerId = generateCustomerId();

const dishes = Object.entries(formData)
  .map(([key, value]) => {
    const match = key.match(/^(.*)\s*-\s*(\d+)\s*Rupees$/);
    if (!match) return null;
    const rawQty = Number(value);
    if (isNaN(rawQty) || rawQty < 1) return null;  // only >1
    const unitPrice = Number(match[2]);
    return {
      dishName: match[1].trim(),
      quantity: rawQty,
      unitPrice,
      totalPrice: rawQty * unitPrice
    };
  })
  .filter(item => item !== null);

return [
  {
    json: {
      customerId,
      name,
      mobile,
      dishes,
    },
  },
];

