// Fetch all incoming items
const items = $input.all();

// Extract the raw row data (each item.json is one row)
const rawRows = items.map(item => item.json);

// Bundle everything into a single field
const payload = { rows: rawRows };

// Return a single output item whose json contains your full dataset
return [{ json: { data: payload } }];

// Step 1: Fetch the raw output from the AI Agent node
const aiResponse = $node["Gemini AI Dish Suggestion Agent"].json.output; // Update "AI Agent" to your node name

// Step 2: Strip Markdown fences if present
const markdownPattern = /^```json\s*([\s\S]*)\s*```$/;
const cleaned = aiResponse.replace(markdownPattern, "$1");

// Step 3: Parse JSON safely
let parsed;
try {
  parsed = JSON.parse(cleaned);
} catch (err) {
  throw new Error(`Failed to parse AI response JSON: ${err.message}`);
}

// Step 4: Return each suggestion as its own item (n8n format)
return parsed.suggestions.map(s => ({
  json: {
    customerId: parsed.customerId,
    ...s
  }
}));
