// Bersihkan dan format data dari spreadsheet
const cleanData = $input.all().map(item => {
  const data = item.json;

  // Parse JSON strings jadi array yang mudah dibaca
  function parseJsonField(field) {
    try {
      if (!field || field === 'not found') return [];
      const parsed = JSON.parse(field);
      if (Array.isArray(parsed)) {
        return parsed.map(item => {
          if (typeof item === 'object') {
            return Object.keys(item).filter(key => item[key] === true);
          }
          return item;
        }).flat();
      }
      return [];
    } catch {
      return [];
    }
  }

  // Parse opening hours jadi format mudah dibaca
  function parseOpeningHours(hoursString) {
    try {
      if (!hoursString) return 'Jam buka tidak tersedia';
      const hours = JSON.parse(hoursString);
      return hours.map(h => `${h.day}: ${h.hours}`).join(', ');
    } catch {
      return 'Jam buka tidak tersedia';
    }
  }

  // Ekstrak data bersih
  const keunggulan = parseJsonField(data.Keunggulan);
  const fasilitas = parseJsonField(data.Fasilitas);
  const pembayaran = parseJsonField(data.Pembayaran);
  const allCategories = parseJsonField(data['All Categories']);
  const openingHours = parseOpeningHours(data['Opening Hours']);

  // Buat deskripsi lengkap untuk AI
  const businessDescription = `
${data.Title} adalah ${data.Category} yang berlokasi di ${data.Neighborhood}. 
Rating: ${data.Rating}/5 dengan ${data.Reviews} review. 
Harga: ${data.Price || 'Tidak diketahui'}.
${data.Description || ''}

Keunggulan: ${keunggulan.join(', ') || 'Tidak ada info keunggulan'}
Fasilitas: ${fasilitas.join(', ') || 'Tidak ada info fasilitas'}  
Metode Pembayaran: ${pembayaran.join(', ') || 'Tidak ada info pembayaran'}
Kategori: ${allCategories.join(', ') || data.Category}
Jam Buka: ${openingHours}

Kontak: ${data.Phone || 'Tidak ada nomor telepon'}
Website: ${data.Website !== 'not found' ? data.Website : 'Tidak ada website'}
Alamat Lengkap: ${data.Address}
`.trim();

  // Hitung lead score
  let leadScore = 0;
  if (data.Rating >= 4.5) leadScore += 30;
  if (data.Rating >= 4.0) leadScore += 20;
  if (data.Reviews >= 1000) leadScore += 25;
  if (data.Reviews >= 100) leadScore += 15;
  if (data.Phone) leadScore += 20;
  if (data.Website && data.Website !== 'not found') leadScore += 20;
  if (keunggulan.length > 0) leadScore += 10;

  return {
    // Data utama
    restaurant_name: data.Title,
    category: data.Category,
    description: data.Description || '',
    location: data.Neighborhood,
    full_address: data.Address,
    phone: data.Phone,
    website: data.Website !== 'not found' ? data.Website : '',
    rating: data.Rating,
    total_reviews: data.Reviews,
    price_range: data.Price || 'Tidak diketahui',

    // Data terformat
    keunggulan_list: keunggulan.join(', '),
    fasilitas_list: fasilitas.join(', '),
    pembayaran_list: pembayaran.join(', '),
    kategori_lengkap: allCategories.join(', '),
    jam_operasional: openingHours,

    // Untuk AI analysis
    business_summary: businessDescription,
    lead_score: leadScore,
    lead_quality: leadScore >= 70 ? 'High' : leadScore >= 40 ? 'Medium' : 'Low',

    // Metadata
    scraped_date: new Date().toLocaleString('id-ID'),
    data_source: 'Google Maps via Apify'
  };
});

return cleanData;

// Transform data yang baru ditambah dari spreadsheet
const newData = $input.all().map(item => {
  const data = item.json;

  // Skip jika data kosong
  if (!data.Title || data.Title.trim() === '') {
    return null;
  }

  // Buat business summary untuk embedding
  const businessSummary = `
${data.Title} adalah ${data.Category || 'restoran'} yang berlokasi di ${data.Neighborhood || 'lokasi tidak diketahui'}. 
Rating: ${data.Rating || 'belum ada rating'}/5 dengan ${data.Reviews || 0} review. 
Harga: ${data.Price || 'Tidak diketahui'}.
${data.Description || ''}

Keunggulan: ${data['Keunggulan Clean'] || 'Tidak ada info keunggulan'}
Fasilitas: ${data['Fasilitas Clean'] || 'Tidak ada info fasilitas'}  
Metode Pembayaran: ${data['Pembayaran Clean'] || 'Tidak ada info pembayaran'}
Jam Operasional: ${data['Jam Operasional'] || 'Tidak tersedia'}

Kontak: ${data.Phone || 'Tidak ada nomor telepon'}
Website: ${data.Website !== 'not found' ? data.Website : 'Tidak ada website'}
Alamat: ${data.Address || 'Alamat tidak lengkap'}
`.trim();

  return {
    // Content untuk embedding
    business_summary: businessSummary,

    // Metadata
    metadata: {
      restaurant_name: data.Title,
      category: data.Category,
      location: data.Neighborhood,
      phone: data.Phone,
      rating: data.Rating,
      total_reviews: data.Reviews,
      lead_score: data['Lead Score'] || 0,
      lead_quality: data['Lead Quality'] || 'Unknown',
      keunggulan: data['Keunggulan Clean'] || '',
      fasilitas: data['Fasilitas Clean'] || '',
      pembayaran: data['Pembayaran Clean'] || '',
      website: data.Website !== 'not found' ? data.Website : '',
      price_range: data.Price || 'Tidak diketahui',
      full_address: data.Address,
      jam_operasional: data['Jam Operasional'] || 'Tidak tersedia',
      row_number: data.row_number,
      last_updated: new Date().toISOString(),
      data_type: "restaurant_lead",
      source: "google_sheets_auto"
    }
  };
}).filter(item => item !== null);

return newData;

// Check dan handle existing data di Supabase vector database
const inputData = $input.all();
const processedData = [];

for (const item of inputData) {
  const data = item.json;

  // Skip jika data kosong atau tidak valid
  if (!data.Title || data.Title.trim() === '') {
    console.log(`Skipping empty data for row ${data.row_number}`);
    continue;
  }

  // Buat unique identifier berdasarkan kombinasi nama + alamat
  const uniqueId = `${data.Title}_${data.Address}`.replace(/[^a-zA-Z0-9]/g, '_').toLowerCase();

  // Buat business summary untuk embedding
  const businessSummary = `
Nama Restoran: ${data.Title}
Kategori: ${data.Category || 'Restoran'}
Lokasi: ${data.Neighborhood || 'Tidak diketahui'}
Alamat: ${data.Address || 'Alamat tidak lengkap'}
Rating: ${data.Rating || 'Belum ada rating'}/5 (${data.Reviews || 0} reviews)
Harga: ${data.Price || 'Tidak diketahui'}
Deskripsi: ${data.Description || 'Tidak ada deskripsi'}

Detail Bisnis:
- Keunggulan: ${data.Keunggulan || 'Tidak ada info keunggulan'}
- Fasilitas: ${data.Fasilitas || 'Tidak ada info fasilitas'}
- Metode Pembayaran: ${data.Pembayaran || 'Tidak ada info pembayaran'}
- Jam Operasional: ${data['Opening Hours'] || 'Tidak tersedia'}

Kontak:
- Telepon: ${data.Phone || 'Tidak ada nomor telepon'}
- Website: ${data.Website && data.Website !== 'not found' ? data.Website : 'Tidak ada website'}

Lead Score: ${data['Lead Score'] || 0}
Lead Quality: ${data['Lead Quality'] || 'Unknown'}
`.trim();

  // Prepare data untuk vector store
  const vectorData = {
    // Text content untuk embedding
    pageContent: businessSummary,

    // Metadata untuk filtering dan searching
    metadata: {
      unique_id: uniqueId,
      restaurant_name: data.Title,
      category: data.Category || 'Restoran',
      location: data.Neighborhood || 'Tidak diketahui',
      full_address: data.Address || '',
      phone: data.Phone || '',
      website: data.Website && data.Website !== 'not found' ? data.Website : '',
      rating: parseFloat(data.Rating) || 0,
      total_reviews: parseInt(data.Reviews) || 0,
      price_range: data.Price || 'Tidak diketahui',
      lead_score: parseInt(data['Lead Score']) || 0,
      lead_quality: data['Lead Quality'] || 'Unknown',

      // Additional business info
      keunggulan: data.Keunggulan || '',
      fasilitas: data.Fasilitas || '',
      pembayaran: data.Pembayaran || '',
      jam_operasional: data['Opening Hours'] || '',

      // System metadata
      row_number: data.row_number,
      data_source: 'google_sheets',
      data_type: 'restaurant_lead',
      last_updated: new Date().toISOString(),

      // Status tracking
      contact_status: 'not_contacted',
      is_active: true
    },

    // Original row data untuk reference
    original_data: data
  };

  // Add flag untuk indicate ini adalah update atau insert baru
  vectorData.metadata.is_update = !!data.row_number;

  processedData.push(vectorData);

  console.log(`Processed: ${data.Title} (ID: ${uniqueId})`);
}

console.log(`Total processed: ${processedData.length} items`);

return processedData;
