// Start from items[1], since items[0] only has metadata
const conversationParts = items[1].json;

const output = [];

for (const key in conversationParts) {
  const part = conversationParts[key];

  if (part && part.part_type === 'comment') {
    output.push({
      json: part,
      pairedItem: {
        item: 1, // link to items[1], where the data came from
      },
    });
  }
}

return output;

function formatTimestamp(unixTime) {
  const date = new Date(unixTime * 1000);
  return date.toISOString().replace('T', ' ').split('.')[0]; // "YYYY-MM-DD HH:mm:ss"
}

let combinedText = items.map(item => {
  const body = item.json.body || '';
  const author = item.json["author.name"] || 'Unknown';
  const role = item.json["author.type"] || 'unknown';
  const timestamp = item.json.created_at ? formatTimestamp(item.json.created_at) : 'Unknown time';

  const cleanBody = body.replace(/<[^>]*>/g, '').trim();

  let speaker = 'Unknown';
  if (role === 'user') {
    speaker = 'User';
  } else if (role === 'bot') {
    speaker = 'Bot';
  } else if (role === 'admin') {
    speaker = 'Admin';
  }

  return `[${timestamp}] ${speaker} (${author}): ${cleanBody}`;
}).join('\n\n');

return [
  {
    json: {
      conversation: combinedText
    }
  }
];

return items.map(item => {
  const data = item.json;

  // Remove HTML tags from body if needed
  const plainBody = data["source.body"]
    ? data["source.body"].replace(/<[^>]*>/g, '').trim()
    : '';

  const name = data["source.author.name"] || 'Unknown';
  const email = data["source.author.email"] || 'unknown@example.com';

  return {
    json: {
      conversationinitiatied: `${name} (${email}): ${plainBody}`
    }
  };
});

let raw = $json["output"];

// Remove Markdown-style code block if present
raw = raw.replace(/^```json\n/, '').replace(/\n```$/, '');

return JSON.parse(raw);
