function formatToReadableDate(dateString) {
try {
const date = new Date(dateString);
if (isNaN(date.getTime())) throw new Error("Invalid date");
const options = {
timeZone: "Europe/Berlin", // Ensures correct local time
month: "long",
day: "numeric",
hour: "numeric",
minute: "2-digit",
hour12: true,
};
const formatted = date.toLocaleString("en-US", options);
// Example output: "June 4, 09:00 AM" ‚Üí make it shorter like
"June 4, 9am"
return formatted
.replace(":00", "") // remove :00 if exact hour
.replace("AM", "am")
.replace("PM", "pm");
} catch (e) {
return `Invalid input: ${e.message}`;
}
}
return [
{
json: {
original: input,
formatted: formatToReadableDate(input),
},
},
];

