const nowParis = new Date(
  new Date().toLocaleString("en-US", { timeZone: "Europe/Paris" })
);
const nowTimestamp = nowParis.getTime();
const twoHoursFromNow = nowTimestamp + 2 * 60 * 60 * 1000;

return items
  .filter(item => {
    const booked = item.json["R√©serv√©"]?.toLowerCase() === "confirm";
    const reminderSent = item.json["Rappel SMS envoy√©"]?.toLowerCase() === "envoyer";
    const rawDate = item.json["Date du rendez-vous"];
    if (!booked || reminderSent || !rawDate) {
      return false;
    }

    const appointmentTimestamp = Date.parse(rawDate);
    return appointmentTimestamp > nowTimestamp &&
           appointmentTimestamp <= twoHoursFromNow;
  })
  .map(item => {
    const rawDate = item.json["Date du rendez-vous"];
    const date = new Date(rawDate);
    if (isNaN(date.getTime())) {
      item.json.appointmentDisplayTime = "(Invalid date)";
      return item;
    }

    const options = {
      timeZone: "Europe/Paris",
      month: "long",
      day: "numeric",
      hour: "numeric",
      minute: "2-digit",
      hour12: true
    };

    const formatted = date.toLocaleString("en-US", options)
      .replace(":00", "")
      .replace("AM", "AM")
      .replace("PM", "PM");

    item.json.appointmentDisplayTime = formatted;
    return item;
  });
