parameters.functionCode:
const items = [];
const places = $input.first().json.places || [];
const searchParams = $node["1. Set Search Parameters"].json;

for (const place of places) {
  const item = {
    id: place.id || '',
    businessName: place.displayName?.text || 'N/A',
    address: place.formattedAddress || 'N/A',
    phone: place.nationalPhoneNumber || 'N/A',
    website: place.websiteUri || 'N/A',
    rating: place.rating || 0,
    businessStatus: place.businessStatus || 'UNKNOWN',
    primaryType: place.primaryType || searchParams.searchCategory,
    latitude: place.location?.latitude || 0,
    longitude: place.location?.longitude || 0,
    searchLocation: searchParams.locationName,
    searchCategory: searchParams.searchCategory,
    createdAt: new Date().toISOString(),
    leadScore: 0,
    status: 'New Lead'
  };

  // Calculate lead score
  let score = 0;
  if (item.rating >= 4.5) score += 30;
  else if (item.rating >= 4.0) score += 20;
  else if (item.rating >= 3.5) score += 10;

  if (item.website !== 'N/A') score += 25;
  if (item.phone !== 'N/A') score += 20;
  if (item.businessStatus === 'OPERATIONAL') score += 25;

  item.leadScore = score;

  items.push({ json: item });
}

return items;
