const allItems = $input.all();
const processingItems = allItems.filter(item => 
    item.json.Status === "processing" && 
    item.json.Keyword && item.json.Keyword.trim() !== "" &&
    item.json['Example Title 1'] && item.json['Example Title 1'].trim() !== ""
);

let selectedItem = null;
if (processingItems.length > 0) {
    const randomIndex = Math.floor(Math.random() * processingItems.length);
    selectedItem = processingItems[randomIndex];
}

return selectedItem ? [selectedItem] : [];

// List of common "extra" characters writers add
const cleanupChars = [
  '—',  // em dash
  '–',  // en dash
  '—',
  '…',  // ellipsis
  '“', '”', '‘', '’',  // fancy quotes
  '\u200B', // zero-width space
  '\u00A0', // non-breaking space
];

// Function to clean HTML content but keep tags
function cleanHtml(html) {
  if (!html) return html;

  let cleaned = html;

  // Remove unwanted characters
  cleanupChars.forEach(char => {
    cleaned = cleaned.split(char).join('');
  });

  // Collapse multiple spaces/newlines (but preserve tags)
  cleaned = cleaned.replace(/>\s+</g, '><') // remove spaces between tags
                   .replace(/\s{2,}/g, ' ') // collapse multiple spaces
                   .trim();

  return cleaned;
}

// Process each item
return items.map(item => {
  const newItem = { ...item };

  // Replace this with your actual HTML field
  const htmlContent = $('AI Agent').first().json.output.content;

  if (htmlContent) {
    newItem.json.output = newItem.json.output || {};
    newItem.json.output.content = cleanHtml(htmlContent);
  }

  return newItem;
});
