// Step 1: Get data from the previous node
const inputData = items[0].json;

// Extract plan names and pricing blocks
const planNames = inputData["Plan name"];
const pricingList = inputData["Pricing"];

// Regex to match entries like "$2,300USD/month"
const priceRegex = /\$[\d,]+USD\/month/;

// Step 2: Extract only valid price strings
const prices = pricingList.filter(item => typeof item === 'string' && priceRegex.test(item));

// Clean prices: "$2,300USD/month" -> 2300 (number)
const cleanedPrices = prices.map(p => {
  const match = p.match(/\$[\d,]+/);
  if (match) {
    return parseFloat(match[0].replace(/[$,]/g, ''));
  }
  return null;
}).filter(p => p !== null);

// Step 3: Build result object
const planPriceMap = {};

for (let i = 0; i < planNames.length; i++) {
  const plan = planNames[i];

  if (['Basic', 'Grow', 'Advanced', 'Plus'].includes(plan) && !(plan in planPriceMap)) {
    planPriceMap[plan] = cleanedPrices.shift() ?? null;
  }
}

// Step 4: Return final structured item
return [
  {
    json: planPriceMap
  }
];

