// Dynamic Pricing Configuration & Product Management
// Created: 25/7/2025

const pricingConfig = {
  // Product catalog with pricing parameters
  products: [
    {
      id: 'PROD-001',
      name: 'Wireless Bluetooth Headphones',
      category: 'Electronics',
      current_price: 79.99,
      cost_price: 35.00,
      min_margin: 0.25, // 25% minimum margin
      max_margin: 0.65, // 65% maximum margin
      min_price: 45.00,
      max_price: 120.00,
      inventory_level: 150,
      monthly_sales: 45,
      competitor_products: [
        'Sony WH-CH720N',
        'JBL Tune 760NC',
        'Sennheiser HD 450BT'
      ],
      search_keywords: ['wireless headphones', 'bluetooth headphones', 'noise canceling']
    },
    {
      id: 'PROD-002',
      name: 'Smart Fitness Tracker',
      category: 'Wearables',
      current_price: 149.99,
      cost_price: 65.00,
      min_margin: 0.30,
      max_margin: 0.70,
      min_price: 85.00,
      max_price: 200.00,
      inventory_level: 75,
      monthly_sales: 28,
      competitor_products: [
        'Fitbit Charge 5',
        'Garmin Vivosmart 4',
        'Samsung Galaxy Fit2'
      ],
      search_keywords: ['fitness tracker', 'smart watch', 'activity monitor']
    },
    {
      id: 'PROD-003',
      name: 'Portable Phone Charger',
      category: 'Accessories',
      current_price: 29.99,
      cost_price: 12.00,
      min_margin: 0.20,
      max_margin: 0.60,
      min_price: 15.00,
      max_price: 45.00,
      inventory_level: 300,
      monthly_sales: 120,
      competitor_products: [
        'Anker PowerCore 10000',
        'RAVPower 20000mAh',
        'Belkin Boost Charge'
      ],
      search_keywords: ['power bank', 'portable charger', 'phone battery']
    }
  ],

  // Competitor monitoring configuration
  competitors: {
    'Amazon': {
      base_url: 'https://www.amazon.com/s?k=',
      price_selector: '.a-price-whole',
      weight: 0.35 // Influence on pricing decisions
    },
    'Best Buy': {
      base_url: 'https://www.bestbuy.com/site/searchpage.jsp?st=',
      price_selector: '.visually-hidden',
      weight: 0.25
    },
    'Walmart': {
      base_url: 'https://www.walmart.com/search?q=',
      price_selector: '[data-automation-id="product-price"]',
      weight: 0.20
    },
    'Target': {
      base_url: 'https://www.target.com/s?searchTerm=',
      price_selector: '[data-test="product-price"]',
      weight: 0.20
    }
  },

  // Pricing strategy parameters
  strategies: {
    aggressive: {
      description: 'Beat competitor prices by 5-10%',
      price_adjustment: -0.075, // Average 7.5% below competitors
      margin_tolerance: 0.15, // Accept 15% margin reduction
      inventory_threshold: 100 // Use when inventory > 100
    },
    competitive: {
      description: 'Match competitor average prices',
      price_adjustment: 0.00, // Match competitor average
      margin_tolerance: 0.05, // Accept 5% margin reduction
      inventory_threshold: 50 // Use when inventory 50-100
    },
    premium: {
      description: 'Price 10-20% above competitors',
      price_adjustment: 0.15, // 15% above competitors
      margin_tolerance: -0.10, // Allow 10% margin increase
      inventory_threshold: 0 // Use when inventory < 50
    }
  },

  // Market demand indicators
  demand_factors: {
    seasonality: {
      'Q4': 1.3, // Holiday season boost
      'Q1': 0.8, // Post-holiday decline
      'Q2': 1.0, // Normal demand
      'Q3': 1.1  // Back-to-school boost
    },
    day_of_week: {
      'Monday': 0.9,
      'Tuesday': 0.95,
      'Wednesday': 1.0,
      'Thursday': 1.05,
      'Friday': 1.2, // Weekend shopping
      'Saturday': 1.3,
      'Sunday': 1.1
    },
    time_of_day: {
      'morning': 0.8,   // 6AM-12PM
      'afternoon': 1.2, // 12PM-6PM
      'evening': 1.4,   // 6PM-10PM
      'night': 0.6      // 10PM-6AM
    }
  },

  // Revenue optimization settings
  optimization: {
    revenue_weight: 0.6,  // 60% focus on revenue
    margin_weight: 0.25,  // 25% focus on margin
    velocity_weight: 0.15, // 15% focus on sales velocity

    // Price change constraints
    max_price_change: 0.15, // Maximum 15% price change per hour
    min_change_threshold: 0.02, // Only change price if >2% difference
    price_change_frequency: 1, // Hours between price changes

    // Performance thresholds
    low_performance_threshold: 0.7, // Sales velocity < 70% of target
    high_performance_threshold: 1.3  // Sales velocity > 130% of target
  }
};

// Get current market context
const now = new Date();
const currentHour = now.getHours();
const currentDay = now.toLocaleDateString('en-US', { weekday: 'long' });
const currentQuarter = Math.ceil((now.getMonth() + 1) / 3);

// Determine time-based demand factors
let timeOfDay = 'night';
if (currentHour >= 6 && currentHour < 12) timeOfDay = 'morning';
else if (currentHour >= 12 && currentHour < 18) timeOfDay = 'afternoon';
else if (currentHour >= 18 && currentHour < 22) timeOfDay = 'evening';

const demandMultiplier = 
  pricingConfig.demand_factors.seasonality[`Q${currentQuarter}`] *
  pricingConfig.demand_factors.day_of_week[currentDay] *
  pricingConfig.demand_factors.time_of_day[timeOfDay];

// Generate competitor monitoring tasks
const monitoringTasks = [];
const competitors = Object.keys(pricingConfig.competitors);

// Create monitoring tasks for each product-competitor combination
pricingConfig.products.forEach(product => {
  competitors.forEach(competitor => {
    product.search_keywords.forEach(keyword => {
      monitoringTasks.push({
        product_id: product.id,
        product_name: product.name,
        competitor: competitor,
        search_keyword: keyword,
        search_url: pricingConfig.competitors[competitor].base_url + encodeURIComponent(keyword),
        competitor_weight: pricingConfig.competitors[competitor].weight,
        current_price: product.current_price,
        cost_price: product.cost_price,
        inventory_level: product.inventory_level,
        monthly_sales: product.monthly_sales
      });
    });
  });
});

// Session tracking
const sessionId = `pricing_${Date.now()}`;
const timestamp = now.toISOString();

// Generate comprehensive monitoring configuration
return [{
  json: {
    sessionId: sessionId,
    timestamp: timestamp,
    market_context: {
      current_hour: currentHour,
      current_day: currentDay,
      current_quarter: currentQuarter,
      time_of_day: timeOfDay,
      demand_multiplier: demandMultiplier
    },
    config: pricingConfig,
    monitoring_tasks: monitoringTasks,
    total_monitoring_tasks: monitoringTasks.length,
    products_count: pricingConfig.products.length,
    competitors_count: competitors.length
  }
}];

// Task Processing and Competitor URL Generation
const items = $input.all();
const configData = items[0].json;
const batchData = $('Split Monitoring Tasks').item.json;

// Get current monitoring task
const currentTask = configData.monitoring_tasks[batchData.index];

// Add market context to current task
const enhancedTask = {
  ...currentTask,
  ...configData.market_context,
  config: configData.config,
  batchIndex: batchData.index,
  totalBatches: configData.monitoring_tasks.length
};

return [{
  json: enhancedTask
}];

// Advanced Pricing Optimization Algorithm
const allData = $input.all();
const taskData = allData[0].json;
const competitorData = allData[1]?.json?.result || {};
const demandData = allData[2]?.json?.result || {};
const sentimentData = allData[3]?.json?.result || {};

// Advanced pricing optimization engine
const pricingOptimizer = {
  // Calculate optimal price based on multiple factors
  calculateOptimalPrice(currentPrice, competitorPrices, demandFactors, customerSentiment, productData) {
    const factors = {
      competitor_influence: 0.35,
      demand_influence: 0.25,
      sentiment_influence: 0.20,
      inventory_influence: 0.10,
      margin_influence: 0.10
    };

    // Competitor pricing analysis
    const competitorScore = this.analyzeCompetitorPricing(currentPrice, competitorPrices);

    // Demand analysis
    const demandScore = this.analyzeDemand(demandFactors, taskData.demand_multiplier);

    // Customer sentiment analysis
    const sentimentScore = this.analyzeSentiment(customerSentiment, currentPrice);

    // Inventory level analysis
    const inventoryScore = this.analyzeInventory(productData.inventory_level, productData.monthly_sales);

    // Margin protection analysis
    const marginScore = this.analyzeMargin(currentPrice, productData.cost_price);

    // Calculate weighted optimization score
    const optimizationScore = 
      (competitorScore * factors.competitor_influence) +
      (demandScore * factors.demand_influence) +
      (sentimentScore * factors.sentiment_influence) +
      (inventoryScore * factors.inventory_influence) +
      (marginScore * factors.margin_influence);

    // Convert score to price adjustment
    const priceAdjustment = this.scoreToPrice(optimizationScore, currentPrice, productData);

    return {
      current_price: currentPrice,
      recommended_price: priceAdjustment.new_price,
      price_change: priceAdjustment.change_amount,
      price_change_percentage: priceAdjustment.change_percentage,
      optimization_score: optimizationScore,
      factor_scores: {
        competitor: competitorScore,
        demand: demandScore,
        sentiment: sentimentScore,
        inventory: inventoryScore,
        margin: marginScore
      },
      confidence_level: this.calculateConfidence(competitorPrices, demandFactors, customerSentiment),
      strategy_recommendation: this.recommendStrategy(optimizationScore, productData)
    };
  },

  // Analyze competitor pricing impact
  analyzeCompetitorPricing(currentPrice, competitorData) {
    if (!competitorData.price_statistics) return 0;

    const avgCompetitorPrice = competitorData.price_statistics.avg_price;
    const minCompetitorPrice = competitorData.price_statistics.min_price;
    const maxCompetitorPrice = competitorData.price_statistics.max_price;

    // Calculate position relative to competitors
    const relativePosition = (currentPrice - avgCompetitorPrice) / avgCompetitorPrice;

    // Score based on competitive position
    if (relativePosition > 0.15) return -0.8; // Too expensive, reduce price
    if (relativePosition > 0.05) return -0.4; // Slightly expensive
    if (relativePosition > -0.05) return 0.0; // Competitive
    if (relativePosition > -0.15) return 0.3; // Good value
    return 0.6; // Excellent value, can increase
  },

  // Analyze demand factors
  analyzeDemand(demandData, demandMultiplier) {
    let demandScore = 0;

    // Search volume trend impact
    if (demandData.search_volume_trend === 'rising') demandScore += 0.4;
    else if (demandData.search_volume_trend === 'falling') demandScore -= 0.4;

    // Interest score impact
    const interestScore = (demandData.interest_score || 50) / 100;
    demandScore += (interestScore - 0.5) * 0.6;

    // Seasonal/time-based demand multiplier
    demandScore += (demandMultiplier - 1.0) * 0.5;

    return Math.max(-1, Math.min(1, demandScore));
  },

  // Analyze customer sentiment
  analyzeSentiment(sentimentData, currentPrice) {
    if (!sentimentData.price_elasticity_indicators) return 0;

    const priceElasticity = sentimentData.price_elasticity_indicators;
    let sentimentScore = 0;

    // Price sensitivity analysis
    const priceSensitivity = priceElasticity.price_sensitivity_score || 0.5;
    sentimentScore -= (priceSensitivity - 0.5) * 0.8;

    // Willingness to pay premium
    const premiumWillingness = priceElasticity.willingness_to_pay_premium || 0.3;
    sentimentScore += (premiumWillingness - 0.3) * 0.6;

    // Brand premium acceptance
    const brandPremium = priceElasticity.brand_premium_acceptance || 0.4;
    sentimentScore += (brandPremium - 0.4) * 0.4;

    return Math.max(-1, Math.min(1, sentimentScore));
  },

  // Analyze inventory levels
  analyzeInventory(inventoryLevel, monthlySales) {
    const monthsOfInventory = inventoryLevel / (monthlySales || 1);

    if (monthsOfInventory > 6) return -0.6; // Excess inventory, reduce price
    if (monthsOfInventory > 3) return -0.3; // High inventory
    if (monthsOfInventory > 1) return 0.0;  // Normal inventory
    if (monthsOfInventory > 0.5) return 0.4; // Low inventory, increase price
    return 0.8; // Very low inventory
  },

  // Analyze margin protection
  analyzeMargin(currentPrice, costPrice) {
    const currentMargin = (currentPrice - costPrice) / currentPrice;

    if (currentMargin < 0.2) return 0.8;  // Low margin, increase price
    if (currentMargin < 0.3) return 0.4;  // Acceptable margin
    if (currentMargin < 0.5) return 0.0;  // Good margin
    if (currentMargin < 0.6) return -0.2; // High margin, can reduce
    return -0.4; // Very high margin
  },

  // Convert optimization score to price adjustment
  scoreToPrice(score, currentPrice, productData) {
    // Base price adjustment percentage
    const baseAdjustment = score * 0.15; // Max 15% adjustment

    // Apply constraints
    const maxChange = taskData.config.optimization.max_price_change;
    const constrainedAdjustment = Math.max(-maxChange, Math.min(maxChange, baseAdjustment));

    // Calculate new price
    const newPrice = currentPrice * (1 + constrainedAdjustment);

    // Apply product-specific constraints
    const finalPrice = Math.max(
      productData.min_price || 0,
      Math.min(productData.max_price || 9999, newPrice)
    );

    // Check minimum change threshold
    const changeAmount = finalPrice - currentPrice;
    const changePercentage = changeAmount / currentPrice;

    if (Math.abs(changePercentage) < taskData.config.optimization.min_change_threshold) {
      return {
        new_price: currentPrice, // No change
        change_amount: 0,
        change_percentage: 0
      };
    }

    return {
      new_price: Math.round(finalPrice * 100) / 100, // Round to 2 decimals
      change_amount: Math.round(changeAmount * 100) / 100,
      change_percentage: Math.round(changePercentage * 10000) / 100 // Percentage with 2 decimals
    };
  },

  // Calculate confidence level
  calculateConfidence(competitorData, demandData, sentimentData) {
    let confidence = 0.5; // Base confidence

    // More data = higher confidence
    if (competitorData.products_found?.length > 5) confidence += 0.2;
    if (demandData.interest_score > 70) confidence += 0.15;
    if (sentimentData.total_reviews_analyzed > 100) confidence += 0.15;

    return Math.min(1.0, confidence);
  },

  // Recommend pricing strategy
  recommendStrategy(optimizationScore, productData) {
    const inventory = productData.inventory_level;
    const strategies = taskData.config.strategies;

    if (optimizationScore < -0.3 && inventory > strategies.aggressive.inventory_threshold) {
      return 'aggressive';
    } else if (optimizationScore > 0.3 && inventory < strategies.premium.inventory_threshold) {
      return 'premium';
    } else {
      return 'competitive';
    }
  }
};

// Execute pricing optimization
const productData = {
  inventory_level: taskData.inventory_level,
  monthly_sales: taskData.monthly_sales,
  cost_price: taskData.cost_price,
  min_price: taskData.config.products.find(p => p.id === taskData.product_id)?.min_price,
  max_price: taskData.config.products.find(p => p.id === taskData.product_id)?.max_price
};

const pricingRecommendation = pricingOptimizer.calculateOptimalPrice(
  taskData.current_price,
  competitorData,
  demandData,
  sentimentData,
  productData
);

// Calculate revenue projections
const revenueProjection = {
  current_revenue_estimate: taskData.current_price * taskData.monthly_sales,
  projected_revenue_estimate: pricingRecommendation.recommended_price * taskData.monthly_sales * (1 + (demandData.interest_score || 50) / 500),
  revenue_change_estimate: 0
};
revenueProjection.revenue_change_estimate = revenueProjection.projected_revenue_estimate - revenueProjection.current_revenue_estimate;

// Compile comprehensive pricing analysis
const pricingAnalysis = {
  session_id: taskData.sessionId,
  timestamp: new Date().toISOString(),
  product_info: {
    id: taskData.product_id,
    name: taskData.product_name,
    category: taskData.config.products.find(p => p.id === taskData.product_id)?.category,
    current_price: taskData.current_price,
    cost_price: taskData.cost_price
  },
  market_analysis: {
    competitor: taskData.competitor,
    search_keyword: taskData.search_keyword,
    competitor_data: competitorData,
    demand_data: demandData,
    sentiment_data: sentimentData
  },
  pricing_recommendation: pricingRecommendation,
  revenue_projection: revenueProjection,
  market_context: taskData.market_context || {},
  batch_info: {
    batch_index: taskData.batchIndex,
    total_batches: taskData.totalBatches
  }
};

return [{ json: pricingAnalysis }];
