parameters.text:
=Here's a new email that came in: {{ $('Gmail Trigger').item.json.id }}

Subject : {{ $('Gmail Trigger').item.json.headers.subject }}
From : {{ $('Gmail Trigger').item.json.headers.from }}
Email body : {{ $('Gmail Trigger').item.json.text }}

parameters.inputSchema:
{
	"type": "object",
	"properties": {
		"emailType": {
			"type": "boolean",
            "description": "True if email is customer receipts and False if vendor invoice"
		},
      "emailBody": {
        "type": "string",
        "description": "Summary of the email content"
      },
    "Amount":{
      "type": "number",
      "description": "Amount stated in the email to be paid or have received"
	},
      "From": {
        "type": "string",
        "description":"who the email is from"
      },
        "DueDate": {
        "type": "string",
        "description":"invoice payment due date"
      }
  }
}

parameters.text:
=üì• New lead: {{ $('Gmail Trigger').item.json.from.value[0].name }}
I searched the knowledge database and drafted a reply for you. Please check your email!

Facts used:
{{ $json.output.knowledgeDatabase }}
Rationale in email reply:
{{ $json.output.reason }}

parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "emailBody": {
      "type": "string",
      "description": "The HTML-formatted body content of the email reply to be sent to the prospect. Use <p>, <br>, and <a> tags for formatting. If escalate is true, this is not required."
    },
    "reason": {
      "type": "string",
      "description": "Short explanation for why this email response was generated. For example, what the user asked for, what info was found, and why this response was appropriate."
    },
    "escalate": {
      "type": "boolean",
      "description": "True if unsure or can't confidently answer the question using available knowledge. False if confident in response and can proceed without human help."
    },
    "knowledgeDatabase": {
      "type": "string",
      "description": "A summary of the facts retrieved from the Knowledge Database that were used to answer the email."
    }
  }
}

parameters.jsonSchemaExample:
{
	"type": "object",
	"properties": {
      "emailBody": {
        "type": "string",
        "description": "Summary of the email content"
      },
      "From": {
        "type": "string",
        "description":"who the email is from"
      }
  }
}
