const transferNotes = {
  metadata: {
    created: new Date().toISOString(),
    lastUpdated: new Date().toISOString(),
    totalFiles: 0,
    successfulTransfers: 0,
    failedTransfers: 0,
    skippedFiles: 0
  },
  settings: {
    maxFileSizeMB: 50,
    allowedExtensions: ['.pdf', '.doc', '.docx', '.txt', '.jpg', '.png', '.zip', '.xlsx'],
    autoDeleteAfterTransfer: false,
    verifyTransfer: true
  },
  transfers: [],
  notes: {
    general: 'File transfer from Google Drive to FTP server',
    lastRun: new Date().toISOString(),
    instructions: 'This workflow automatically transfers files from Google Drive to FTP'
  }
};

const driveFiles = $input.all();
const validFiles = [];

for (const fileItem of driveFiles) {
  const file = fileItem.json;
  const fileName = file.name;
  const fileSize = parseInt(file.size) || 0;
  const fileExtension = fileName.substring(fileName.lastIndexOf('.')).toLowerCase();

  const maxSizeBytes = transferNotes.settings.maxFileSizeMB * 1024 * 1024;
  const isAllowedExtension = transferNotes.settings.allowedExtensions.includes(fileExtension);
  const isSizeOk = fileSize <= maxSizeBytes;

  if (isAllowedExtension && isSizeOk) {
    validFiles.push({
      id: file.id,
      name: fileName,
      size: fileSize,
      extension: fileExtension,
      modifiedTime: file.modifiedTime,
      mimeType: file.mimeType,
      transferStatus: 'pending'
    });
  } else {
    transferNotes.transfers.push({
      timestamp: new Date().toISOString(),
      fileId: file.id,
      fileName: fileName,
      fileSize: fileSize,
      status: 'skipped',
      reason: !isAllowedExtension ? 'Invalid file extension' : 'File too large',
      details: {
        extension: fileExtension,
        sizeMB: Math.round(fileSize / 1024 / 1024 * 100) / 100
      }
    });
    transferNotes.metadata.skippedFiles++;
  }
}

transferNotes.metadata.totalFiles = validFiles.length;

return [{
  json: {
    transferNotes: transferNotes,
    validFiles: validFiles,
    totalFilesFound: driveFiles.length,
    validFilesCount: validFiles.length
  }
}];

const currentFile = $('split-files').item.json.validFiles[$('split-files').item.json.batchIndex];
const transferNotes = $('filter-files').item.json.transferNotes;

const transferRecord = {
  timestamp: new Date().toISOString(),
  fileId: currentFile.id,
  fileName: currentFile.name,
  fileSize: currentFile.size,
  status: 'success',
  transferDuration: null,
  ftpPath: `/remote/directory/${currentFile.name}`,
  details: {
    extension: currentFile.extension,
    mimeType: currentFile.mimeType,
    sizeMB: Math.round(currentFile.size / 1024 / 1024 * 100) / 100
  }
};

transferNotes.transfers.push(transferRecord);
transferNotes.metadata.successfulTransfers++;
transferNotes.metadata.lastUpdated = new Date().toISOString();

return [{
  json: {
    transferNotes: transferNotes,
    currentTransfer: transferRecord,
    message: `Successfully transferred file: ${currentFile.name}`
  }
}];

const currentFile = $('split-files').item.json.validFiles[$('split-files').item.json.batchIndex];
const transferNotes = $('filter-files').item.json.transferNotes;
const errorInfo = $input.last().error || 'Unknown error';

const transferRecord = {
  timestamp: new Date().toISOString(),
  fileId: currentFile.id,
  fileName: currentFile.name,
  fileSize: currentFile.size,
  status: 'failed',
  errorMessage: errorInfo.message || errorInfo,
  details: {
    extension: currentFile.extension,
    mimeType: currentFile.mimeType,
    sizeMB: Math.round(currentFile.size / 1024 / 1024 * 100) / 100
  }
};

transferNotes.transfers.push(transferRecord);
transferNotes.metadata.failedTransfers++;
transferNotes.metadata.lastUpdated = new Date().toISOString();

return [{
  json: {
    transferNotes: transferNotes,
    currentTransfer: transferRecord,
    message: `Error during file transfer: ${currentFile.name} - ${errorInfo.message || errorInfo}`
  }
}];

const transferNotes = $json.transferNotes;
const summary = {
  executionDate: new Date().toLocaleString('en-US'),
  totalFiles: transferNotes.metadata.totalFiles,
  successful: transferNotes.metadata.successfulTransfers,
  failed: transferNotes.metadata.failedTransfers,
  skipped: transferNotes.metadata.skippedFiles,
  successRate: transferNotes.metadata.totalFiles > 0 ? Math.round((transferNotes.metadata.successfulTransfers / transferNotes.metadata.totalFiles) * 100) : 0,
  settings: transferNotes.settings,
  lastUpdated: transferNotes.metadata.lastUpdated
};

const successfulTransfers = transferNotes.transfers.filter(t => t.status === 'success').map(t => ({
  fileName: t.fileName,
  sizeMB: t.details.sizeMB,
  timestamp: t.timestamp
}));

const failedTransfers = transferNotes.transfers.filter(t => t.status === 'failed').map(t => ({
  fileName: t.fileName,
  error: t.errorMessage,
  timestamp: t.timestamp
}));

return [{
  json: {
    summary: summary,
    successfulTransfers: successfulTransfers,
    failedTransfers: failedTransfers,
    fullNotes: transferNotes
  }
}];
