console.log("postUrl: ", postUrl)
const commentText = $input.first().json.output;

// Clean utility
const clean = str => String(str || "").replace(/[\u200B-\u200D\uFEFF]/g, "").trim();

// Escape and quote single data row
const row = [
  `"${clean(postUrl).replace(/"/g, '""')}"`,
  `"${clean(commentText).replace(/"/g, '""')}"`
];

// Convert to buffer without header or BOM
const csvBuffer = Buffer.from(row.join(",") + "\n", "utf8");

// Return binary file
return [
  {
    binary: {
      data: {
        data: csvBuffer,
        mimeType: "text/csv",
        fileName: "phantombuster_clean.csv"
      }
    }
  }
];

const randomPost = normalizeUrl($('Get Random Post').first().json.postUrl);
const allItems = $('Extract from File').all();

if (!allItems || allItems.length === 0) {
  return [{ json: { isDuplicate: false } }];
}

function normalizeUrl(url) {
  if (typeof url !== 'string') {
    console.log("🚨 NOT A STRING:", url);
    return '';
  }
  return url.trim().toLowerCase().replace(/\/$/, '');
}

// Helper function to get postUrl value regardless of BOM
function getPostUrl(item) {
  const json = item.json;
  // Try normal field name first
  if (json.postUrl) return json.postUrl;

  // Look for any field containing "postUrl"
  const postUrlKey = Object.keys(json).find(key => key.includes('postUrl'));
  return postUrlKey ? json[postUrlKey] : null;
}

console.log("✅ randomPost:", randomPost);
console.log("✅ allItems:", allItems);

let isDuplicate = false;

for (const item of allItems) {
  const candidateRaw = getPostUrl(item);
  const candidate = normalizeUrl(candidateRaw);

  console.log(`🟠 Comparing:\n→ raw: ${candidateRaw}\n→ normalized: ${candidate}\n→ target: ${randomPost}`);

  if (candidate === randomPost) {
    isDuplicate = true;
    break;
  }
}

return [{ json: { isDuplicate } }];

const existingUrls = $('Extract from File').all().map(item => {
  // Get the first key (which has the BOM) and use its value
  const firstKey = Object.keys(item.json)[0];
  return { postUrl: item.json[firstKey] };
});

console.log("existingUrls: ", existingUrls);

const randomPost = $('Get Random Post').first().json.postUrl;
console.log("randomPost: ", randomPost);

return [
  ...existingUrls,
  { postUrl: randomPost }
].map(entry => ({ json: entry }));

